insert into contabilidade.planocontadetalhe values (5030, 1, '1.1.3.8.1.06.00.00.00.00', 'VALORES EM TRÂNSITO REALIZÁVEIS A CURTO PRAZO', 'Compreende o somatório dos valores contabilizados indevidamente em conta bancária, guia de recebimento e ordens bancárias emitidas a compensar.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5031, 1, '1.1.3.8.1.07.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE INFRAÇÕES LEGAIS E CONTRATUAIS', 'Registra os recursos a receber provenientes da aplicação de autos de infração ou decorrentes de quebra de cláusulas contratuais. Os créditos decorrentes da quebra de cláusulas contratuais podem ser pagos em espécie, glosa ou através de fornecimentos.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5032, 1, '1.1.3.8.1.08.00.00.00.00', 'CRÉDITOS A RECEBER POR REEMBOLSO DE SALÁRIO FAMÍLIA PAGO', 'Registra os valores relativos ao salário-família, incluídos em folhas de pagamento, pagos aos empregados (CLT), com filhos menores, a compensar com as contribuições devidas pelo empregador a Previdência Social.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5033, 1, '1.1.3.8.1.09.00.00.00.00', 'CRÉDITOS A RECEBER POR REEMBOLSO DE SALÁRIO MATERNIDADE PAGO', 'Registra os salários pagos as servidoras gestantes, regidas pela CLT no período que perdurar a licença maternidade, prevista em lei, a deduzir das obrigações sociais devidas pelos órgãos e entidades.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5034, 1, '1.1.3.8.1.10.00.00.00.00', 'AUXÍLIO NATALIDADE PAGO A RECUPERAR', 'Registra os valores relativos à auxilio natalidade, pagos aos empregados regidos pela CLT, quando do nascimento de filhos destes, a recuperar, por se tratar de obrigações da Previdência Social.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5035, 1, '1.1.3.8.1.11.00.00.00.00', 'CRÉDITOS A RECEBER POR REEMBOLSO DE AUXÍLIO DOENÇA E ACIDENTES PAGOS', 'Registra os salários pagos aos servidores, regidos pela CLT, a título de auxilio doença/acidentes, previsto em lei, a serem reembolsados pelo INSS.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5036, 1, '1.1.3.8.1.12.00.00.00.00', 'CRÉDITOS A RECEBER POR CESSÃO DE PESSOAL', 'Registra os valores de créditos a receber tais como: folha de pagamento, despesa de transporte, alimentação e outras relativas à cessão de pessoal.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5037, 1, '1.1.3.8.1.13.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE CESSÃO DE ÁREAS PÚBLICAS', 'Registra os valores dos créditos a receber decorrentes da cessão de áreas públicas.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5038, 1, '1.1.3.8.1.14.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE ALIENAÇÃO DE BENS MÓVEIS E IMÓVEIS', 'Registra os valores dos créditos a receber decorrentes da alienação de bens móveis ou imóveis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5039, 1, '1.1.3.8.1.15.00.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE PAGAMENTO DE DESPESA DE TERCEIROS', 'Registra os recursos a receber provenientes de pagamentos de despesas de terceiros decorrentes de cláusulas contratuais, tais como: água, luz, telefone, instalações, transporte e aluguéis.', 1, true, 2, 'P');
