insert into contabilidade.planocontadetalhe values (5053, 1, '1.1.4.1.1.09.02.00.00.00', 'TÍTULOS DE RESPONSABILIDADE DO BANCO CENTRAL', 'Registra saldos remanescentes dos investimentos realizados pelo RPPS, em títulos de emissão do Banco Central do Brasil, considerando que este não emite mais títulos da Dívida Pública, conforme artº 34 da Lei Complementar nº 101/2000.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5054, 1, '1.1.4.1.1.09.03.00.00.00', 'DEPÓSITOS DE POUPANÇA', 'Registra os investimentos realizados pelo RPPS em poupança e seus rendimentos (juros e atualização monetária) posteriores, em conformidade com as Resoluções CMN n° 3.992/2010 e 4392/2014.', 1, true, 2, 'F');
