insert into contabilidade.planocontadetalhe values (5056, 1, '1.1.4.1.1.09.05.00.00.00', 'FUNDOS DE INVESTIMENTO EM RENDA FIXA - CRÉDITO PRIVADO', 'Registra os investimentos realizados pelos RPPS em fundos de investimentos em renda fixa - crédito privado, efetuados em conformidade com as Resoluções CMN n° 3.992/2010 e 4392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5057, 1, '1.1.4.1.1.09.06.00.00.00', 'FUNDOS DE INVESTIMENTOS EM RENDA FIXA OU REFERENCIADOS - IMA/IDkA', 'Registra os investimentos realizados pelo RPPS em fundos de investimentos ou como referenciados em indicadores de desempenho de renda fixa, constituídos sob a forma de condomínio aberto e cuja política de investimento assuma o compromisso de buscar o retorno de um dos subíndices do Índice de Mercado Anbima (IMA) ou do Índice de Duração Constante Anbima (IDkA), efetuados em conformidade com as Resoluções CMN n° 3.992/2010 e 4392/2014.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5058, 1, '1.1.4.1.1.09.07.00.00.00', 'FUNDOS DE INVESTIMENTOS EM DIREITOS CREDITORIOS ', 'Registra os investimentos realizados pelo RPPS em fundos de investimentos em direito creditório, efetuados em conformidade com as Resoluções CMN n° 3.992/2010 e 4392/2014.', 1, true, 2, 'F');
