insert into contabilidade.planocontadetalhe values (5138, 1, '1.2.1.1.5.03.09.00.00.00', 'PROGRAMA DE REESTRUTURAÇÃO FISCAL DOS MUNICÍPIOS - CRÉDITOS A RECEBER', 'Registra os valores a receber decorrentes da assunção e refinanciamento, pela União, de dívidas de responsabilidade dos Municípios, nos termos da MP nº 2.185-35, de 24 de agosto de 2001.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5139, 1, '1.2.1.1.5.03.10.00.00.00', 'JUROS E ENCARGOS A RECEBER - PROGRAMA DE REESTRUTURAÇÃO FISCAL DOS MUNICÍPIOS', 'Registra os juros e encargos a receber decorrentes da assunção e refinanciamento, pela União, de dívidas de responsabilidade  dos Municípios, nos termos da MP nº 2.185-35, de 24 de agosto de 2001.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5140, 1, '1.2.1.1.5.04.00.00.00.00', 'DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende os valores dos créditos de dívida ativa tributária inscritos, realizáveis após os doze meses seguintes a data de publicação dos demonstrativos contábeis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5141, 1, '1.2.1.1.5.05.00.00.00.00', 'DÍVIDA ATIVA NÃO TRIBUTÁRIA', 'Compreende os valores dos créditos de dívida ativa não tributária inscritos, realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5142, 1, '1.2.1.9.1.06.00.00.00.00', 'CONTRIBUIÇÕES CONFEDERATIVAS A APROPRIAR', 'Compreende os valores pagos a titulo de contribuições confederativas a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5143, 1, '1.2.1.2.1.01.01.00.00.00', 'ADIANTAMENTOS CONCEDIDOS A PESSOAL', 'Registra os valores das antecipações concedidas a servidores e a dirigentes da unidade, tais como antecipações de salários e ordenados, adiantamentos de 13º salário, adiantamentos de férias e de gratificações natalinas,entre outras.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5144, 1, '1.2.1.2.1.02.02.00.00.00', 'ICMS A RECUPERAR/COMPENSAR', 'Registra o somatório dos valores de ICMS a recuperar/compensar.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5145, 1, '1.2.1.2.1.02.03.00.00.00', 'ICMS DIFERIDO', 'Registra os valores do ICMS diferido dos estoques.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5146, 1, '1.2.1.2.1.02.04.00.00.00', 'IRRF A  COMPENSAR', 'Registra o somatório dos valores do IR retido na fonte a compensar.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5147, 1, '1.2.1.2.1.02.06.00.00.00', 'IR E CSLL DIFERIDOS', 'Registra a parcela do IR e CSLL que representa a diferença entre os valores de lucro apurados seguindo as normas fiscais e seguindo o regime de competência, quando estes forem menores e as diferenças temporárias.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5148, 1, '1.2.1.2.1.02.07.00.00.00', 'ISS A  COMPENSAR', 'Registra o somatório dos valores do ISS a compensar.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5149, 1, '1.2.1.2.1.03.00.00.00.00', 'CRÉDITOS A RECEBER POR DESCENTRALIZAÇÃO DA PRESTAÇÃO DE SERVIÇOS PÚBLICOS', 'Compreende os créditos a receber com realização após os doze meses seguintes a data de publicação das demonstrações contábeis, decorrentes da descentralização de serviços públicos, tanto por meio de outorga a entidades da administração indireta como por meio de delegação, nas formas de concessão, permissão ou autorização ao particular do direito de exploração de serviços públicos.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5150, 1, '1.2.1.2.1.04.01.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE FOLHA DE PAGAMENTO', 'Registra o crédito do ente correspondente a débitos administrativos decorrentes de folha de pagamento.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5151, 1, '1.2.1.2.1.04.02.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE DANO AO PATRIMONIO', 'Registra o crédito do ente correspondente a débito administrativo decorrentes de danos ao patrimônio público.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5152, 1, '1.2.1.2.1.04.03.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE ERRO ADMINISTRATIVO', 'Registra o crédito do ente correspondente a débito administrativo decorrentes de erro da administração.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5153, 1, '1.2.1.2.1.04.04.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE DOLO, MA-FE OU FRAUDE', 'Registra o crédito do ente correspondente a débito administrativo decorrentes de dolo, má-fé ou fraude.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5154, 1, '1.2.1.2.1.04.05.00.00.00', 'CREDITOS A RECEBER DE SERVIDORES NAO RECOLHIDOS NO PRAZO LEGAL', 'Registra os saldos dos valores entregues a funcionários ou servidores, não devolvidos dentro dos prazos estabelecidos em normas legais ou determinação administrativa ou seja, quando o produto a arrecadar não tenha sido recolhido no prazo legal.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5155, 1, '1.2.1.2.1.04.06.00.00.00', 'CREDITOS A RECEBER POR DEBITOS DE TERCEIROS EM PRESTAÇÃO DE SERVICOS', 'Registra os valores relativos as responsabilidades por irregularidades em prestação de serviços por terceiros, cujos processos foram apurados, com imputação da responsabilidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (6420, 1, '7.1.1.3.1.03.00.00.00.00', 'CONTRATOS DE ALUGUÉIS', 'Registra o valor do contrato de aluguéis em que o poder público seja o locador do bem.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (5156, 1, '1.2.1.2.1.04.07.00.00.00', 'MULTAS E JUROS A RECEBER DE SERVIDORES RESPONSABILIZADOS', 'Registra os valores referentes a multas e juros pagos pelo órgão, porém de responsabilidade de servidores, que foram apurados, com imputação da responsabilidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5157, 1, '1.2.1.2.1.04.08.00.00.00', 'CREDITOS A RECEBER POR USO INDEVIDO DE COTA', 'Registra os débitos (apurados e em apuração) de parlamentares em razão de recebimento indevido de valor relacionado a cota destina a custear gastos vinculados ao exercício de atividade parlamentar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5158, 1, '1.2.1.2.1.04.09.00.00.00', 'CREDITOS A RECEBER ORIUNDOS DE USO OU ALUGUEL ', 'Registra os débitos (apurados e em apuração) referentes a débitos de parlamentares conhecidos após seu afastamento do mandado e que não puderam ser descontados em folha de pagamento. exemplos: remanescente de conta de energia elétrica, gás, taxa de limpeza pública e aluguéis de outros bens imóveis pertencente ao órgão.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5159, 1, '1.2.1.9.1.07.00.00.00.00', 'BENEFÍCIOS A APROPRIAR', 'Compreende os valores pagos a titulo de beneficio a pessoal a apropriar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5160, 1, '1.2.1.2.1.04.10.00.00.00', 'CREDITOS A RECEBER POR PAGAMENTOS INDEVIDOS DE BENEFICIOS PREVIDENCIARIOS  ', 'Registra as responsabilidades imputadas a órgãos pagadores por pagamentos indevidos de benefícios previdenciários, pós-morte do segurado (créditos reconhecidos sem necessidade de instauração de tomada de contas especial).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5161, 1, '1.2.1.2.1.04.99.00.00.00', 'OUTROS CREDITOS ADMINISTRATIVOS', 'Registra o crédito do ente correspondente a outros créditos administrativos que não se enquadram em rubricas específicas existentes.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5162, 1, '1.2.1.2.1.05.01.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE PAGAMENTOS INDEVIDOS -TOMADA DE CONTAS ESPECIAL', 'Registra as responsabilidades imputadas a gestores e outros responsáveis e co-responsáveis por pagamentos efetuados indevidamente, inclusive em casos de super faturamento. (quando for instaurada tomada de conta especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5163, 1, '1.2.1.2.1.05.02.00.00.00', 'CREDITOS A RECEBER DE SERVIDORES NAO RECOLHIDOS NO PRAZO LEGAL  - TOMADA DE CONTAS ESPECIAL', 'Registra os saldos dos valores entregues a funcionários ou servidores, não devolvidos dentro dos prazos estabelecidos em normas legais ou determimanação administrativa, ou seja, quando o produto a arrecadar não tenha sido recolhido no prazo legal.(quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5164, 1, '1.2.1.2.1.05.03.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE DESFALQUES OU DESVIOS  - TOMADA DE CONTAS ESPECIAL', 'Registra os desfalques, desvios de bens e valores da ente público ou de entidades publicas, apuradas em processos por ações administrativas e policial ou julgamento judicial. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5165, 1, '1.2.1.2.1.05.04.00.00.00', 'CREDITOS A RECEBER POR DEBITOS DE TERCEIROS EM PRESTAÇÃO DE SERVICOS - TOMADA DE CONTAS ESPECIAL', 'Registra os valores relativos às responsabilidades por irregularidades em prestação de serviços por terceiros, cujos processos foram apurados, com imputação da responsabilidade. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5166, 1, '1.2.2.1.2.01.08.00.00.00', 'PARTICIPAÇÃO EM FUNDOS', 'Registra as participações permanentes do ente em fundos, avaliadas por equivalência patrimonial.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5167, 1, '1.2.1.2.1.05.05.00.00.00', 'CREDITOS A RECEBER DE RESPONSAVEIS POR DANOS OU PERDAS - TOMADA DE CONTAS ESPECIAL', 'Registra as responsabilidades apuradas em inquéritos administrativo ou policial contra servidores ou terceiros, por danos e perdas causados afazenda nacional sendo apurados com imputação da responsabilidade. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5168, 1, '1.2.1.2.1.05.06.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE PAGAMENTOS SEM RESPALDO ORCAMENTARIO  - TOMADA DE CONTAS ESPECIAL', 'Registra as despesas realizadas sem existência de crédito que as comporte, resultando em pagamento sem prévio empenho ou ainda com classificação imprópria (diferente daquela prevista no orçamento), apuradas, com imputação da responsabilidade (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5169, 1, '1.2.1.2.1.05.07.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE CREDITOS ANTECIPADOS  - TOMADA DE CONTAS ESPECIAL', 'Registra os valores pagos sobre dotações decorrentes de créditos antecipados não realizados até o fechamento do exercício, apurados, com imputacao da responsabilidade (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5170, 1, '1.2.1.2.1.05.08.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE FALTA OU IRREGULARIDADE DE COMPROVAÇÃO  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores correspondentes à falta de documentação comprobatória da execução da despesa, inclusive as apresentações fora do prazo legal, bem como a inadimplência na comprovação de instrumentos de transferência ou ainda irregularidade na documentação apurados, com imputação da responsabilidade (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5171, 1, '1.2.2.1.1.02.01.00.00.00', 'PARTICIPAÇÕES EM OUTRAS SOCIEDADES', 'Registra as participações permanentes do ente em outras sociedades em forma de ações ou cotas, avaliadas pelo custo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5172, 1, '1.2.1.2.1.05.09.00.00.00', 'CREDITOS  A RECEBER DECORRENTES DE FALTA DE LICITAÇÃO  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores relativos a bens e serviços adquiridos sem a necessária licitação específica, em processos motivados, por determinações administrativas e ou inquéritos, apurados, com imputação da responsabilidade. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5173, 1, '1.2.1.2.1.05.10.00.00.00', 'MULTAS E JUROS A RECEBER DE SERVIDORES RESPONSABILIZADOS - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores referentes a multas e juros pagos pelo órgão, porém de responsabilidade de servidores, que foram apurados, com imputação da responsabilidade .(quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5174, 1, '1.2.1.2.1.05.11.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE FALTA DE RECOLHIMENTO DE OBRIGAÇÕES PECUNIARIAS  - TOMADA DE CONTAS ESPECIAL ', 'Registra os valores correspondentes à falta de recolhimento ou pagamento de obrigações pecuniárias, tais como: parte de obrigações patronais do empregador , valores descontados de terceiros e não recolhidos, não pagamento de obrigações para com o ente. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5175, 1, '1.2.1.2.1.05.12.00.00.00', 'CREDITOS A RECEBER DECORRENTES DE FRAUDES EM BENEFICIOS  - TOMADA DE CONTAS ESPECIAL ', 'Registra os débitos relativos a fraudes em benefícios da previdência social, com a responsabilidade devidamente apurada e imputada a servidores ou terceiros (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5204, 1, '1.2.1.3.1.03.00.00.00.00', 'APLICAÇÕES EM SEGMENTO DE IMÓVEIS', 'Compreende os investimentos de longo prazo realizados em segmento de imóveis, realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5176, 1, '1.2.1.2.1.05.13.00.00.00', 'CREDITOS  A RECEBER DECORRENTES DE ADIANTAMENTOS CONCEDIDOS IRREGULARMENTE   - TOMADA DE CONTAS ESPECIAL ', 'Registra as responsabilidades imputadas a gestores e outros responsáveis e co-responsáveis por adiantamentos concedidos em desacordo com a legislação pertinente, tais como a fornecedores e empregados.(quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5177, 1, '1.2.1.2.1.05.14.00.00.00', 'CREDITOS A RECEBER POR USO INDEVIDO DE COTA - TOMADA DE CONTAS ESPECIAL ', 'Registra os debitos (apurados e em apuração) de parlamentares em razão de recebimento indevido de valor relacionado a cota destinada a custear gastos vinculados ao exercício de atividade parlamentar.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5178, 1, '1.2.1.2.1.05.15.00.00.00', 'CREDITOS A RECEBER ORIUNDOS DE USO OU ALUGUEL - TOMADA DE CONTAS ESPECIAL ', 'Registra os débitos (apurados e em apuração) referentes a débitos de parlamentares conhecidos após seu afastamento do mandado e que não puderam ser descontados em folha de pagamento. exemplos: remanescente de conta de energia elétrica, gás, taxa de limpeza pública e aluguéis de outros bens imóveis pertencente ao órgão.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5179, 1, '1.2.1.2.1.05.99.00.00.00', 'OUTRAS CREDITOS A RECEBER APURADOS EM TOMADA DE CONTAS ESPECIAL ', 'Registra as responsabilidades diversas cujas classificações não estejam definidas em contas próprias ou para as quais não hajam contas específicas. (quando for instaurada tomada de contas especial)', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5180, 1, '1.2.1.2.1.06.01.00.00.00', 'DEPÓSITOS E CAUÇÕES RELATIVOS A CONTRATOS OU CONVENÇÕES', 'Registra os valores relativos a depósitos e cauções, efetuados por vinculações a contratos ou convenções, para garantias de operações específicas, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5181, 1, '1.2.1.2.1.06.02.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Registra os valores a receber decorrentes de depósitos efetuados por determinação judicial, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5182, 1, '1.2.1.2.1.06.03.00.00.00', 'DEPÓSITOS PARA INTERPOSIÇÃO DE RECURSOS', 'Registra os valores relativos aos depósitos vinculados decorrentes da interposição de recursos na esfera judicial ou administrativa.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5183, 1, '1.2.1.2.1.06.04.00.00.00', 'DEPÓSITOS ESPECIAIS', 'Registra os valores relativos a outros depósitos em contas bancárias, vinculados a obrigações contratuais, convenções, acordos e ajustes.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5184, 1, '1.2.1.2.1.06.05.00.00.00', 'VALORES APREENDIDOS POR DECISÃO JUDICIAL', 'Registra os valores apreendidos por decisão judicial através de penhora ou arresto, realizáveis no longo prazo.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5185, 1, '1.2.1.2.1.06.06.00.00.00', 'DEPÓSITOS TRANSFERIDOS', 'Registra os valores relativos a depósitos transferidos, para garantia de operações específicas.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5186, 1, '1.2.1.2.1.06.07.00.00.00', 'DEPÓSITOS COMPULSÓRIOS', 'Registra os valores relativos a depósitos compulsórios, de acordo com a legislação em vigor.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5187, 1, '1.2.1.2.1.06.99.00.00.00', 'OUTROS DEPÓSITOS RESTITUÍVEIS E VALORES VINCULADOS', 'Registra os valores relativos a outros depósitos restituíveis e valores vinculados.', 1, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (5188, 1, '1.2.1.2.1.98.01.00.00.00', 'CRÉDITOS A RECEBER DE ENTIDADES FEDERAIS', 'Registra os valores realizáveis após 12 (doze) meses da data das demonstrações decorrentes de débito de entidades federais com o estado/município.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5189, 1, '1.2.1.2.1.98.02.00.00.00', 'CRÉDITOS A RECEBER POR CESSÃO DE CRÉDITOS', 'Registra os valores dos créditos a receber cedidos pelo estado/município realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5190, 1, '1.2.1.2.1.98.03.00.00.00', 'CRÉDITOS ORIUNDOS DE CONCESSÕES DE DIREITOS DE USO E DE EXPLORAÇÃO DE BENS', 'Registra o valor dos créditos oriundos das concessões de direitos de uso e de exploração de bens do estado/município, para terceiros ou para outras entidades governamentais realizáveis no longo prazo.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5191, 1, '1.2.1.2.1.98.04.00.00.00', 'CRÉDITOS A RECEBER DE DEVEDORES DE CONVÊNIOS', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, oriundos de créditos de convênios.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5192, 1, '1.2.1.2.1.98.05.00.00.00', 'CRÉDITOS A RECEBER DECORRENTES DE ALIENAÇÃO DE BENS MÓVEIS E IMÓVEIS', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, dos créditos a receber decorrentes da alienação de bens móveis e imóveis.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5193, 1, '1.2.2.1.5.01.06.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL', 'Registra os valores repassados pelo ente, destinados a serem utilizados para aumento de capital.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5194, 1, '1.2.1.2.1.98.06.00.00.00', 'CRÉDITOS A RECEBER POR ACERTO FINANCEIRO COM SERVIDOR E EX-SERVIDOR', 'Registra os valores, realizáveis após 12 (doze) meses da data das demonstrações, dos créditos a receber decorrentes de acertos financeiros com servidores e ex-servidores do órgão.', 1, true, 2, 'P');
