insert into contabilidade.planocontadetalhe values (5420, 1, '1.2.3.2.1.05.08.00.00.00', 'REDES DE TELECOMUNICAÇÕES', 'Registra os valores de redes de telecomunicações que foram construídos utilizando-se recursos públicos e que estejam sob controle ou administração da unidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5421, 1, '1.2.3.2.1.05.09.00.00.00', 'BENS DO PATRIMONIO CULTURAL', 'Bens com significancia histórica, cultural ou ambiental tais como monumentos e prédios históricos, sítios arqueológicos, área de conservação e reservas naturais e obras de arte, quando passíveis de mensuração e avaliação.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5422, 1, '1.2.3.2.1.05.99.00.00.00', 'OUTROS BENS DE USO COMUM DO POVO', 'Registra os valores bens de uso comum, não classificados anteriormente neste plano de contas, que foram adquiridos, produzidos ou construídos utilizando-se recursos públicos e que estejam sob controle ou administração da unidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5423, 1, '1.2.3.2.1.06.01.00.00.00', 'OBRAS EM ANDAMENTO', 'Registra os valores pertinentes a obras, desde sua construção até o término da mesma.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5424, 1, '1.2.3.2.1.06.05.00.00.00', 'ESTUDOS E PROJETOS', 'Registra os valores relativos a estudos e projetos, englobando limpeza do terreno, serviços topográficos, sondagens de reconhecimento, terraplenagem, drenagens, instalações provisórias como galpões, instalações elétricas e hidráulicas, utilizadas durante as obras.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5425, 1, '1.2.3.2.1.07.00.00.00.00', 'INSTALAÇÕES', 'Registra equipamentos, materiais e custo de implantação de instalações que, não obstante integradas aos edifícios, devem ser segregadas das obras civis, como, por exemplo, as instalações elétricas, hidráulicas, sanitárias, de vapor, de ar comprimido, frigoríficas, contra incêndio, de comunicações, de climatização, para combustíveis, gases, de antipoluição, para cozinha, dentre outros. E também aplicável a construção de trechos ferroviários.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5426, 1, '1.2.3.2.1.08.00.00.00.00', 'BENFEITORIAS EM PROPRIEDADE DE TERCEIROS', 'Registra construções em terrenos arrendados de terceiros e instalações e outras benfeitorias em prédios alugados. São considerados somente os gastos com construção e instalação que se incorporam ao imóvel e revertem ao proprietário do imóvel ao final da locação.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5427, 1, '1.2.3.2.1.99.02.00.00.00', 'IMÓVEIS EM PODER DE TERCEIROS', 'Registra os imóveis concedidos a terceiros a título de empréstimo gratuito com prazo de devolução convencionado.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5428, 1, '1.2.3.2.1.99.03.00.00.00', 'MATERIAIS TEMPORARIAMENTE SEPARADOS DE IMÓVEIS ', 'Registra os materiais provisoriamente separados de um imóvel, para nele se reempregarem.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5429, 1, '1.2.3.2.1.99.05.00.00.00', 'BENS IMÓVEIS A CLASSIFICAR', 'Registra os bens imóveis, cuja classificação esteja pendente por algum motivo, geralmente por falta de identificação ou pendência de legalização ou registro.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5430, 1, '1.2.3.2.1.99.06.00.00.00', 'BENS IMÓVEIS A ALIENAR', 'Registra os valores relativos aos bens imóveis separados para alienação, quando considerados desnecessários, inservíveis ou por decisão administrativa da unidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5431, 1, '1.2.3.2.1.99.99.00.00.00', 'OUTROS BENS IMÓVEIS', 'Registra os valores relativos a outros bens imóveis, para os quais não tenham sido criadas contas próprias ou específicas neste plano de contas.', 1, true, 2, 'P');
