insert into contabilidade.planocontadetalhe values (5434, 1, '1.2.4.1.1.02.00.00.00.00', 'SOFTWARES EM DESENVOLVIMENTO', 'Registra os valores apropriados na fase de desenvolvimento do software, de acordo com a nota técnica específica para o tema.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5435, 1, '1.2.4.2.1.01.00.00.00.00', 'MARCAS E PATENTES INDUSTRIAIS', 'Registra os valores relativos a bens intangíveis, englobando os gastos com registro de marca, nome, invenções próprias, além de desembolsos a terceiros por contratos de uso de marcas, patentes ou processos de fábricação (tecnologia).', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5436, 1, '1.2.4.2.1.02.00.00.00.00', 'CONCESSÃO DE DIREITO DE USO DE COMUNICAÇÃO', 'Registra os valores relativos a bens intangíveis representados pela concessão de direito de uso de comunicação e divulgação.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5437, 1, '1.2.4.2.1.03.00.00.00.00', 'DIREITOS AUTORAIS ', 'Registra os valores relativos aos direitos autorais adquiridos pela entidade.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5438, 1, '1.2.4.2.1.04.00.00.00.00', 'DIREITOS SOBRE RECURSOS NATURAIS', 'Registra os valores relativos aos direitos obtidos por legislação e/ou por compras de direitos de reservas ou de exploração de recursos naturais, tais como jazidas e outros.', 1, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (5439, 1, '1.2.4.2.1.05.00.00.00.00', 'ADIANTAMENTO PARA TRANFERÊNCIA DE TECNOLOGIA', 'Registra todos os adiantamentos a fornecedores vinculados a um contrato de transferencia de tecnologia. Representam pagamentos por conta de um valor previamente contratado.', 1, true, 2, 'P');
