insert into contabilidade.planocontadetalhe values (5852, 1, '3.3.2.3.1.19.00.00.00.00', 'CONFECÇÃO DE MATERIAL DE ACONDIC. E EMBALAGEM', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de confecção de materiais destinados a preservação, acomodação ou embalagem de produtos diversos, tais como: - bolsas - caixas - mochilas - sacolas e outros. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5853, 1, '3.3.2.3.1.20.00.00.00.00', 'CONFECÇÃO DE UNIFORMES, BANDEIRAS E FLAMULAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de confecção de: - bandeiras - brasoes - estandartes - flamulas - uniformes (inclusive as despesas relacionadas com auxílio fardamento descritos na lei num.8237/91) e outros. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5854, 1, '3.3.2.3.1.21.00.00.00.00', 'DIREITOS AUTORAIS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de direitos autorais sobre obras cientificas, literárias ou em que a divulgação seja de interesse do governo.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5855, 1, '3.3.2.3.1.22.00.00.00.00', 'EXPOSIÇÕES, CONGRESSOS, CONFERENCIAS E OUTROS', 'Registra as variações patrimoniais diminutivas provenientes de serviços utilizados na instalação e manutenção de: conferencias - reuniões técnicas - congressos - exposições - feiras - festejos populares - festivais - e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5856, 1, '3.3.2.3.1.23.00.00.00.00', 'FESTIVIDADES E HOMENAGENS', 'Registra as variações patrimoniais diminutivas provenientes de serviços utilizados na organização de eventos, tais como: coquetéis - festas de congraçamento - recepções - e outras.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5857, 1, '3.3.2.3.1.24.00.00.00.00', 'HONORARIOS ADVOCATICIOS - ONUS DE SUCUMBENCIA', 'Registra as variações patrimoniais diminutivas provenientes da apropriação dos honorários advocatícios decorrentes do ônus da sucumbência, entendido este como o ônus que recai sobre a parte vencida numa ação, para pagamento dos honorários advocatícios a vencedora.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5858, 1, '3.3.2.3.1.25.00.00.00.00', 'HOSPEDAGENS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de hospedagens e alimentação de servidores e convidados do governo em viagens oficiais pagos diretamente a estabelecimentos hoteleiros (quando não houver pagamento de diárias).', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5859, 1, '3.3.2.3.1.26.00.00.00.00', 'PATROCINIO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de patrocínios a atividades ou eventos, de natureza cultural, esportiva, educacional, ambiental, cientifica, comunitária, promocional etc. Para influenciar favoravelmente o público e/ou atingir outros objetivos relacionados com a promoção de atos, obras, planos, ações, bens ou serviços. Não inclui o patrocínio de mídia, assim considerados aqueles em que o proponente e um veículo de comunicação ou um gestor de meios ou instalações que funcionem como veículo de mídia alternativa e aqueles em que um veículo de comunicação e o único ou o principal realizador da ação patrocinada, ainda que o proponente do projeto e provedor de conteúdo para a ação patrocinada não se enquadre na definição de veículo de comunicação ou meio de mídia alternativa.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5860, 1, '3.3.2.3.1.27.00.00.00.00', 'PRODUÇÕES JORNALISTICAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de edição de jornais, revistas, noticiários e materiais jornalísticos para vídeos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5861, 1, '3.3.2.3.1.28.00.00.00.00', 'PROMOÇÃO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços realizados por integrantes do sicom ou por terceiros, que empreguem recursos de não-mídia, realizadas com objetivo de incentivar públicos de interesse a conhecerem ou comprarem produtos, serviços, marcas, conceitos ou políticas públicas.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5862, 1, '3.5.1.2.2.02.02.00.00.00', 'DOAÇÕES CONCEDIDAS DE BENS IMÓVEIS', 'Registra o valor de doações de bens imóveis entre unidades de órgãos diferentes.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5863, 1, '3.3.2.3.1.29.00.00.00.00', 'SEGUROS EM GERAL', 'Registra as variações patrimoniais diminutivas provenientes da apropriação de prêmios pagos por seguros de qualquer natureza, inclusive cobertura de danos causados a pessoas ou bens de terceiros, prêmios de seguros de bens do estado ou de terceiros, seguro obrigatório de veículos.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5864, 1, '3.3.2.3.1.30.00.00.00.00', 'SELEÇÃO E TREINAMENTO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços prestados nas áreas de instrução e orientação profissional, recrutamento e seleção de pessoal (concurso público) e treinamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5865, 1, '3.3.2.3.1.55.00.00.00.00', 'ARRENDAMENTO MERCANTIL OPERACIONAL', 'Registra as variações patrimoniais diminutivas provenientes de arrendamento mercantil operacional', 1, true, 2, 'N');
