insert into contabilidade.planocontadetalhe values (8634, 1, '8.1.2.1.1.03.10.00.00.00', 'OUTRAS CONTRAGARANTIAS CONCEDIDAS NO PAÍS A EXECUTAR', 'Registra o valor de outras contragarantias pendentes de execução, no país.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (5867, 1, '3.3.2.3.1.31.00.00.00.00', 'SERV.MEDICO-HOSPITALAR,ODONTOL.E LABORATORIAIS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços médico-hospitalares, odontológicos e laboratoriais, prestados por pessoas jurídicas sem vínculo empregatício, tais como: analises clinicas - cirurgias - consultas - ecografias - endoscopias -enfermagem - esterilização - exames de laboratório - raio x -tomografias - tratamento odontológico - ultrassonografias - e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5868, 1, '3.3.2.3.1.32.00.00.00.00', 'SERVIÇOS BANCARIOS', 'Registra as variações patrimoniais diminutivas provenientes da apropriação de comissões, tarifas e remunerações decorrentes de serviços prestados por bancos e outras instituições financeiras, bem como despesas com aguarda/custodia de valores (cofres de aluguel).', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5869, 1, '3.3.2.3.1.33.00.00.00.00', 'SERVIÇOS DE ANALISES E PESQUISA CIENTIFICAS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de analises físico-químicas e pesquisas cientificas, não relacionadas com apoio ao ensino. - Análises minerais - analises de solo - analises químicas - coleta de dados em experimentos - tratamento e destinação de resíduos e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5870, 1, '3.3.2.3.1.34.00.00.00.00', 'SERVIÇOS DE APOIO DO ENSINO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços com o objetivo de proporcionar o desenvolvimento e o aperfeiçoamento do ensino, em todos os níveis, inclusive pesquisas experiências e assemelhados.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5871, 1, '3.3.2.3.1.35.00.00.00.00', 'SERVIÇOS DE ASSISTENCIA SOCIAL', 'Registra as variações patrimoniais diminutivas provenientes de serviços de assistência social prestada a servidores, segurados carentes, abrigados, internados e a seus dependentes, tais como: - ajuda de custo supletiva - gêneros alimentícios - documentação -transporte e sepultamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5872, 1, '3.4.1.1.1.01.00.00.00.00', 'JUROS DA DÍVIDA CONTR.C/INSTIT.FINANCEIRAS', 'Registra a variação patrimonial diminutiva com juros da dívida pública derivada de obrigações assumidas com instituições financeiras com vistas a execução de obras, fornecimento de bens ou prestação de serviços, ou da celebração de contratos de financiamento.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5873, 1, '3.3.2.3.1.36.00.00.00.00', 'SERVIÇOS DE AUDIO VIDEO E FOTO', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de filmagens, gravações, revelações, ampliações e reproduções de sons e imagens. - Confecção de álbuns - confecção de crachás funcionais por firmas especializadas - emolduramento de fotografias - imagens de satélites- revelação de filmes - microfilmagem e outros.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5874, 1, '3.3.2.3.1.37.00.00.00.00', 'SERVIÇOS DE CONFECÇÃO SELOS CONTROLE FISCAL', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de confecção de selos/chancelas para controle fiscal.', 1, true, 2, 'N');
