insert into contabilidade.planocontadetalhe values (5876, 1, '3.3.2.3.1.39.00.00.00.00', 'SERVIÇOS DE CONTROLE AMBIENTAL', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de controle ambiental.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5877, 1, '3.3.2.3.1.40.00.00.00.00', 'SERVIÇOS DE COPIAS E REPRODUÇÃO DE DOCUMENTOS', 'Registra as variações patrimoniais diminutivas provenientes da utilização de serviços de copias xerográficas, inclusive a locação e a manutenção de equipamentos reprográficos, copias e reproduções de documentos e publicações em geral, inclusive por meio magnético e digitalização.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5878, 1, '3.3.2.3.1.41.00.00.00.00', 'SERVIÇOS DE CRECHE E ASSISTENCIA PRE ESCOLAR', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços prestados por entidades de assistência social para atender os dependentes de servidores do órgão, habilitados a usufruírem desse benefício.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5879, 1, '3.3.2.3.1.42.00.00.00.00', 'SERVIÇOS DE INCINERAÇÃO/DESTRUIÇÃO DE MATERIAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviço de processo de incineração e destruição de materiais, bem como resíduos industriais.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5880, 1, '3.3.2.3.1.43.00.00.00.00', 'SERVIÇOS DE PRODUÇÃO INDUSTRIAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de transformação, beneficiamento e industrialização de matérias-primas que resultarão em um produto final. Somente serão classificados como serviços, caso a unidade forneça a matéria-prima.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5881, 1, '3.3.2.3.1.44.00.00.00.00', 'SERVIÇOS DE REABILITAÇÃO PROFISSIONAL', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de reabilitação profissional prestados por pessoa jurídica, com o objetivo de proporcionar aos incapacitados (parcial ou totalmente), os meios indicados para a reeducação e readaptação profissional e social, de modo que possam voltar a participar do mercado de trabalho. Ex.: contratação de empresa para ministrar curso profissionalizante.', 1, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (5882, 1, '3.3.2.3.1.45.00.00.00.00', 'SERVIÇOS FUNERARIOS', 'Registra as variações patrimoniais diminutivas proveniente da utilização de serviços de remoções, sepultamentos e transladações.', 1, true, 2, 'N');
