insert into contabilidade.planocontadetalhe values (6427, 1, '7.1.2.1.1.01.03.00.00.00', 'HIPOTECAS', 'Registra o total das garantias de valores por hipotecas concedidas no país.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6428, 1, '7.1.2.1.1.01.04.00.00.00', 'SEGUROS-GARANTIA', 'Registra a garantia de valores por seguros concedidos no país.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6429, 1, '7.1.2.1.1.01.06.00.00.00', 'GARANTIAS INSTITUÍDAS POR LEI ESPECÍFICA', 'Registra o valor referente a garantias concedidas na forma prevista em lei específica, no país e no exterior. Esta categoria inclui as garantias ou riscos assumidos em operações realizadas no âmbito de fundos, programas e operações especiais, no país.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6430, 1, '7.1.2.1.1.01.07.00.00.00', 'GARANTIAS CONCEDIDAS EM CONTRATOS DE PPP', 'Registra o valor das garantias concedidas em contratos de Parceria Público Privadas - PPP.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6431, 1, '7.1.2.1.1.01.99.00.00.00', 'OUTRAS GARANTIAS E CONTRAGARANTIAS CONCEDIDAS NO PAÍS', 'Registra o valor de outras contragarantias concedidas não incluídas nos itens anteriores, sobre obrigações contraídas no país.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6432, 1, '7.1.2.1.1.02.01.00.00.00', 'AVAIS', 'Registra o valor da garantia de pagamento de título de crédito exclusivamente, de natureza pessoal, dada a terceiros, no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6433, 1, '7.1.2.1.1.02.02.00.00.00', 'FIANÇAS', 'Registra o valor da obrigação acessória concedida por órgãos e entidades pelo cumprimento da obrigação do devedor, caso esse não cumpra, no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6434, 1, '7.1.2.1.1.02.03.00.00.00', 'HIPOTECAS', 'Registra o total das garantias de valores por hipotecas concedida no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6435, 1, '7.1.2.1.1.02.04.00.00.00', 'SEGUROS-GARANTIA', 'Registra a garantia de valores por seguros recebidos no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6436, 1, '7.1.2.1.1.02.05.00.00.00', 'CAUÇÕES', 'Registra a responsabilidade da unidade por títulos, valores e bens recebidos em cauções no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6437, 1, '7.1.2.1.1.02.06.00.00.00', 'GARANTIAS INSTITUÍDAS POR LEI ESPECÍFICA', 'Registra o valor referente a garantias concedida na forma prevista em lei específica, no país e no exterior. Esta categoria inclui as garantias ou riscos assumidos em operações realizadas no âmbito de fundos, programas e operações especiais, no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6438, 1, '7.1.2.1.1.02.99.00.00.00', 'OUTRAS GARANTIAS E CONTRAGARANTIAS CONCEDIDAS NO EXTERIOR', 'Registra o valor de outras contragarantias concedida não incluídas nos itens anteriores, sobre obrigações contraídas no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6439, 1, '7.1.2.1.1.03.01.00.00.00', 'AVAIS', 'Registra o valor das contragarantias decorrentes das concessões de avais por obrigações contraídas, no pais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6440, 1, '7.1.2.1.1.03.02.00.00.00', 'FIANÇAS', 'Registra o valor das contragarantias decorrentes das concessões de fianças por obrigações contraídas, no pais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6441, 1, '7.1.2.1.1.03.03.00.00.00', 'CONTRAGARANTIAS INSTITUÍDAS POR LEI ESPECÍFICA', 'Registra o valor das contragarantias em virtude de garantias instituídas em lei especifica concedidas sobre obrigações contraídas no pais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6442, 1, '7.1.2.1.1.03.04.00.00.00', 'OUTRAS CONTRAGARANTIAS CONCEDIDAS NO PAÍS', 'Registra o valor de outras contragarantias concedidas no pais, não incluídas nos itens anteriores.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6443, 1, '7.1.2.1.1.04.01.00.00.00', 'AVAIS', 'Registra o valor das contragarantias decorrentes das concessões de avais por obrigações contraídas, no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6444, 1, '7.1.2.1.1.04.03.00.00.00', 'CONTRAGARANTIAS INSTITUÍDAS POR LEI ESPECÍFICA', 'Registra o valor das contragarantias em virtude de garantias instituídas em lei especifica concedidas sobre obrigações contraídas no exterior.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6445, 1, '7.1.2.1.1.04.99.00.00.00', 'OUTRAS CONTRAGARANTIAS CONCEDIDAS NO EXTERIOR', 'Registra o valor de outras contragarantias concedidas no exterior, não incluídas nos itens anteriores.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6446, 1, '7.1.2.2.1.01.00.00.00.00', 'OBRIGAÇÕES CONVENIADAS ', 'Registra o valor total recebido dos convênios firmados mantendo-o originalmente mesmo após a sua fase de aprovação.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6447, 1, '7.1.2.2.1.02.00.00.00.00', 'TERMO DE COOPERAÇÃO', 'Registra valor total recebido dos termos de cooperação firmados mantendo-o originalmente mesmo após a sua fase de aprovação.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6448, 1, '7.1.2.2.1.99.00.00.00.00', 'OUTROS INSTRUMENTOS CONGÊNERES ', 'Registra o valor total recebido de outros instrumentos congêneres firmados mantendo-o originalmente mesmo após a sua fase de aprovação.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6449, 1, '7.1.2.3.1.01.00.00.00.00', 'CONTRATOS DE SEGUROS', 'Registra o valor do contrato de seguros em que a administração pública participa como contratante no instrumento contratual com terceiros.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6450, 1, '7.1.2.3.1.02.00.00.00.00', 'CONTRATOS DE SERVIÇOS', 'Registra o valor do contrato de serviços em que a administração pública seja a contratante no instrumento contratual com terceiros.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6451, 1, '7.1.2.3.1.03.00.00.00.00', 'CONTRATOS DE ALUGUÉIS', 'Registra o valor do contrato de aluguéis em que o poder público seja o locatário do bem.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6452, 1, '7.1.2.3.1.04.00.00.00.00', 'CONTRATOS DE FORNECIMENTO DE BENS', 'Registra o valor do contrato de fornecimento de bens pela administração pública a terceiros.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6453, 1, '7.1.2.3.1.05.00.00.00.00', 'CONTRATOS DE EMPRÉSTIMOS E FINANCIAMENTOS', 'Registra o valor do contrato de obrigação da entidade pública com terceiros referente a empréstimos e financiamentos contraídos no exercício.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6454, 1, '7.1.2.3.1.06.00.00.00.00', 'CONTRATO DE GESTÃO', 'Registra os contratos de gestão, nos termos do artigo 37, par. 8º da CF/88.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6455, 1, '7.1.2.3.1.08.01.00.00.00', 'OBRIGAÇÕES CONTRATUAIS-CONTRAPRESTAÇÕES FUTURAS', 'Registra a previsão das contraprestações futuras derivadas de PPP contratadas pelo ente da Federação. Devem abranger as despesas com a parcela fixa da contraprestação pecuniária, assim como a parcela variável vinculada ao desempenho do parceiro privado. Deve refletir tanto a parcela referente a incorporação do ativo quanto à parcela referente aos serviços.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6456, 1, '7.1.2.3.1.08.02.00.00.00', 'RISCOS NÃO PROVISIONADOS', 'Compreende os passivos contingentes relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei 11.079/2004, que não tenham sido registradas com Provisões de PPP.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6457, 1, '7.1.2.3.1.08.03.00.00.00', 'OUTROS PASSIVOS CONTINGENTES', 'Registra o valor dos demais passivos contingentes referentes a PPP.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6458, 1, '7.1.2.3.1.09.00.00.00.00', 'CONTRATOS DE RATEIO DE CONSÓRCIOS PÚBLICOS', 'Registra o valor do passivo do ente público consorciado referente aos contratos de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6459, 1, '7.1.2.3.1.10.00.00.00.00', 'CONTRATOS DE PPP - DO ENTE FEDERADO, EXCETO ESTATAIS NÃO DEPENDENTES', 'Registra a previsão do valor, por contrato, das despesas derivadas de PPP contratadas pelo ente da Federação excluídas as empresas estatais não dependentes. Devem abranger as despesas todas as despesas, correntes e de capital, derivadas do conjunto das parcerias contratadas para os próximos 9 anos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6460, 1, '7.1.2.3.1.11.00.00.00.00', 'CONTRATOS DE PPP - DAS ESTATAIS NÃO DEPENDENTES', 'Registra a previsão de valor, por contrato, das despesas derivadas de PPP contratadas pelas empresas estatais não dependentes. Devem abranger as despesas todas as despesas, correntes e de capital, derivadas do conjunto das parcerias contratadas para os próximos 9 anos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6461, 1, '7.2.1.1.1.00.00.00.00.00', 'RECURSOS ORDINÁRIOS', 'Registra as disponibilidades por destinação de recursos ordinários.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6462, 1, '7.2.1.1.2.00.00.00.00.00', 'RECURSOS VINCULADOS ', 'Registra as disponibilidades por destinação de recursos vinculados.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6463, 1, '7.2.1.1.3.00.00.00.00.00', 'RECURSOS EXTRAORÇAMENTÁRIOS', 'Registra as disponibilidades por destinação de recursos extraorçamentários.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6464, 1, '7.3.1.1.1.01.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA TRIBUTÁRIA - IMPOSTOS', 'Registra o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa, referentes a impostos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6465, 1, '7.3.1.1.1.02.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA TRIBUTÁRIA - TAXAS', 'Registra o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa, referentes a taxas', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6466, 1, '7.3.1.1.1.03.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA TRIBUTÁRIA - CONTRIBUIÇÃO DE MELHORIA', 'Registra o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa, referentes a contribuições de melhoria.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6467, 1, '7.3.1.1.2.01.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA  NÃO-TRIBUTÁRIA', 'Registra o montante dos créditos a encaminhar e encaminhados para inscrição em Dívida Ativa Não-Tributária..', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6468, 1, '7.3.2.1.1.00.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA TRIBUTÁRIA', 'Compreende o valor dos créditos de origem tributária passíveis de serem inscritos em dívida ativa pelo órgão competente.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6469, 1, '7.3.2.1.2.00.00.00.00.00', 'INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA NÃO-TRIBUTÁRIA', 'Compreende o valor dos créditos de natureza não-tributára passíveis de serem inscritos em dívida ativa pelo órgão competente.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6470, 1, '7.4.1.1.1.00.00.00.00.00', 'DEMANDAS JUDICIAIS', 'Registra a estimativa do montante relativo a ações judiciais em andamento contra o ente federativo nas quais haja a probabilidade de que o ganho de causa venha a ser da outra parte. Ao fazer a estimativa, considerar os principais tipos de ação judicial, tais como:a) Demandas judiciais contra a atividade reguladora do Estado: Indexação e controles de preços; De ordem tributária e previdenciária; Outras demandas judiciais;b) Demandas judiciais contra empresas estatais dependentes;c) Demandas judiciais contra a administração direta e indireta: Privatizações, liquidação ou extinção de órgãos ou empresas; Reajustes salariais;d) Demandas trabalhistas contra o ente federativo;e) Outras demandas judiciais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6471, 1, '7.4.1.1.2.00.00.00.00.00', 'DÍVIDAS EM PROCESSO DE RECONHECIMENTO', 'Registra as Dívidas ainda não assumidas formalmente que apresentam probabilidade de serem incorporadas ao passivo devido, por exemplo, a decisões judiciais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6472, 1, '8.9.1.2.2.02.00.00.00.00', '(-) DEPRECIAÇÃO DOS ATIVOS CONTABILIZADOS NO ENTE', 'Registra o valor da depreciação dos ativos oriundos de contratos de Parcerias Público Privadas. Tal depreciação deve estar reconhecida no ativo imobilizado do ente (Poder Concedente), mas deve ser registrada em conta de controle a fim de obter a informação segregada dos ativos produzidos por meio de PPP.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6473, 1, '7.4.1.1.3.00.00.00.00.00', 'AVAIS E GARANTIAS CONCEDIDAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo para honrar fianças e avais concedidos em operações de crédito direta ou indiretamente pelo ente federativo a favor de outros entes federativos e entidades dos setores público e privado, inclusive com recursos de fundos de aval, a assunção de risco creditício em linhas de crédito, o seguro de crédito à exportação e outras garantias de natureza semelhante que representem compromisso de adimplência de obrigação financeira ou contratual.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6474, 1, '7.4.1.1.4.00.00.00.00.00', 'ASSUNÇÃO DE PASSIVOS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser gasto pelo ente federativo com o objetivo de proteger o cidadão ou sistemas importantes do Mercado contra inadimplências, falências, altos prejuízos, ou garantir a credibilidade desses sistemas frente a fatores agudos adversos. Ao fazer a estimativa, considerar os principais tipos de situações que podem advir:a) de órgãos da administração direta ou indireta;b) de entidades privadas;c) de Regime Próprio de Previdência Social dos Servidores Públicos;d) de Entes da Federação;e) do Regime Geral de Previdência Social;f) do Banco Central; eg) da assunção de outros passivos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6475, 1, '7.4.1.1.5.00.00.00.00.00', 'ASSISTÊNCIAS DIVERSAS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo com o objetivo de fazer frente a calamidades públicas e que, por não serem recorrentes, não foram planejados. Recomenda-se que o ente federativo explicite o tipo de assistência emergencial que ele está prevendo, por exemplo, se é assistência contra seca, enchentes, catástrofes, epidemias etc.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6476, 1, '7.4.1.1.9.00.00.00.00.00', 'OUTROS PASSIVOS CONTINGENTES', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo para fazer frente a outros tipos de passivos contingentes.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6477, 1, '7.4.2.1.1.00.00.00.00.00', 'FRUSTRAÇÃO DE ARRECADAÇÃO', 'Registra a estimativa do montante de redução de arrecadação que apresenta probabilidade de vir a ocorrer no exercício, decorrente de, por exemplo, cenários macroeconômicos desfavoráveis não previstos na época da elaboração do Orçamento.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6478, 1, '7.4.2.1.2.00.00.00.00.00', 'RESTITUIÇÃO DE TRIBUTOS A MAIOR', 'Registra a estimativa do montante de devolução de tributos a maior que apresenta probabilidade de vir a ocorrer no exercício.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6479, 1, '7.4.2.1.3.00.00.00.00.00', 'DISCREPÂNCIA DE PROJEÇÕES:', 'Registra a estimativa do montante de redução no valor dos ingressos ou de aumento no valor dos desembolsos que apresentam probabilidade de vir a ocorrer no exercício, decorrentes de evolução desfavorável de indicadores econômicos empregados na época da elaboração do Orçamento, tais como:a) Taxa de crescimento econômico;b) Taxa de inflação;c) Taxa de câmbio;d) Taxa de juros;e) Salário mínimo;f) Outros indicadores.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6480, 1, '7.4.2.1.4.00.00.00.00.00', 'OUTROS RISCOS FISCAIS', 'Registra a estimativa do montante que apresenta probabilidade de vir a ser empregado pelo ente federativo para fazer frente a outros tipos de riscos fiscais.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6481, 1, '7.5.2.0.0.00.00.00.00.00', 'PRESTAÇÃO DE CONTAS DE CONSÓRCIOS PÚBLICOS', 'Compreende contas relacionadas ao registro do controle da prestação de contas dos Consórcios Públicos.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6482, 1, '7.5.3.1.0.00.00.00.00.00', 'VALORES TRANSFERIDOS POR CONTRATO DE RATEIO', 'Compreende os valores repassados pelo ente para execução das despesas nos Consórcios Públicos, com base no Contrato de Rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6483, 1, '7.5.3.2.0.00.00.00.00.00', 'DESPESAS EXECUTADAS EM CONSÓRCIOS PÚBLICOS', 'Compreende as despesas executadas em consórcios públicos atribuídas ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6484, 1, '7.5.3.3.0.00.00.00.00.00', 'EXECUÇÃO DE RP NÃO PROCESSADOS EM CONSÓRCIOS PÚBLICOS', 'Compreende o somatório dos valores relativos à transferência, liquidação e pagamento das despesas empenhadas, não liquidadas no exercício financeiro de sua inscrição e não pagas, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6485, 1, '7.5.3.4.0.00.00.00.00.00', 'EXECUÇÃO DE RP PROCESSADOS EM CONSÓRCIOS PÚBLICOS', 'Compreende o somatório dos valores relativos à transferência, liquidação e pagamento das despesas empenhadas, liquidadas no exercício financeiro de sua inscrição e não pagas, em consórcios públicos atribuídos ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6486, 1, '7.5.3.5.0.00.00.00.00.00', 'INSUFICIÊNCIA DE CAIXA DO CONSÓRCIO PÚBLICO', 'Compreende a parcela total da insuficiência de caixa gerada no consórcio público atribuída ao ente consorciado com base no contrato de rateio.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6487, 1, '7.9.1.1.1.00.00.00.00.00', 'DEPÓSITOS JUDICIAIS', 'Registra os depósitos judiciais efetuados por terceiros em títulos, em conta vinculada etc, sob a responsabilidade da unidade, ou depositados por esta para terceiros.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6488, 1, '7.9.1.1.2.00.00.00.00.00', 'DEPÓSITOS COMPULSÓRIOS', 'Registra o somatório dos valores dos depósitos compulsórios realizados.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6489, 1, '7.9.1.1.3.00.00.00.00.00', 'OUTRAS RESPONSABILIDADES COM TERCEIROS', 'Registra responsabilidades com terceiros, não individualizadas nas outras contas desse grupo.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6490, 1, '7.9.1.2.1.00.00.00.00.00', 'CONTROLE DE ADIANTAMENTOS/SUPRIMENTOS DE FUNDOS CONCEDIDOS', 'Registra os valores concedidos a servidor responsável para atender despesas que não possam subordinar-se ao processo normal de aplicação, até a sua prestação de contas.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6491, 1, '7.9.1.2.2.00.00.00.00.00', 'ATIVOS DE PPP CONTABILIZADOS NO ENTE', 'Registra o valor contábil líquido dos ativos produzidos por meio de Parcerias Público Privadas e contabilizados no Ente (Poder Concedente). Deverá ser debitada para fins de incorporação do ativo e deverá ser creditada para fins de registro da depreciação.', 1, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (6492, 1, '7.9.1.2.9.00.00.00.00.00', 'OUTRAS RESPONSABILIDADES DE TERCEIROS', 'Registra o valor de outras responsabilidades de terceiros, de natureza eventual, por títulos, valores e bens, para as quais não há contas especificas.', 1, true, 1, 'N');
