insert into contabilidade.planocontadetalhe values (6494, 1, '1.1.3.6.4.99.00.00.00.00', 'OUTROS CRÉDITOS PREVIDENCIÁRIOS ', 'Registra os valores relativos a outros créditos previdenciários devidos ao RPPS pelo ente, sujeitos a pagamento em parcela única ou parcelados.', 1, false, 0, 'P');
