insert into contabilidade.planocontadetalhe values (6660, 1, '1.1.4.1.0.00.00.00.00.00', 'TÍTULOS E VALORES MOBILIÁRIOS', 'Compreendem as aplicações de recursos em títulos e valores mobiliários, não destinadas à negociação e que não façam parte das atividades operacionais da entidade, resgatáveis no curto prazo.', 1, false, 0, 'N');
