insert into contabilidade.planocontadetalhe values (6729, 1, '1.2.1.2.1.02.00.00.00.00', 'TRIBUTOS A RECUPERAR/COMPENSAR', 'Compreende o somatório dos valores dos tributos a recuperar/compensar.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6730, 1, '1.2.1.2.1.04.00.00.00.00', 'CRÉDITOS POR DANOS AO PATRIMÔNIO PROVENIENTES DE CRÉDITOS ADMINISTRATIVOS', 'Compreende os valores realizáveis após os doze meses seguintes a data de publicação das demonstrações contábeis, provenientes de direitos oriundos de danos ao patrimônio, apurados em processos administrativos.', 1, false, 0, 'N');
