insert into contabilidade.planocontadetalhe values (6742, 1, '1.2.2.0.0.00.00.00.00.00', 'INVESTIMENTOS', 'Compreende as participações permanentes em outras sociedades, bem como os bens e direitos não classificáveis no ativo circulante nem no ativo realizável a longo prazo e que não se destinem a manutenção da atividade da entidade.', 1, false, 0, 'N');
