insert into contabilidade.planocontadetalhe values (6758, 1, '1.2.3.2.1.99.00.00.00.00', 'DEMAIS BENS IMÓVEIS', 'Compreende os demais bens imóveis não classificados anteriormente nesse plano de contas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6759, 1, '1.2.2.2.0.00.00.00.00.00', 'PROPRIEDADES PARA INVESTIMENTO', 'Compreende a propriedade (terreno ou edifício - ou parte de edifício - ou ambos) mantida (pelo proprietário ou pelo arrendatário em arrendamento financeiro) para auferir aluguel ou para valorização do capital ou para ambas, e não para: uso na produção ou fornecimento de bens ou serviços ou para finalidades administrativas; ou venda no curso ordinário do negócio.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6760, 1, '1.2.2.2.1.00.00.00.00.00', 'PROPRIEDADES PARA INVESTIMENTO - CONSOLIDAÇÃO', 'Compreende a propriedade (terreno ou edifício - ou parte de edifício - ou ambos) mantida (pelo proprietário ou pelo arrendatário em arrendamento financeiro) para auferir aluguel ou para valorização do capital ou para ambas, e não para: uso na produção ou fornecimento de bens ou serviços ou para finalidades administrativas; ou venda no curso ordinário do negócio. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6761, 1, '1.2.2.2.1.01.00.00.00.00', 'BENS IMÓVEIS PARA FUTURA UTILIZAÇÃO', 'Compreende os valores de imóveis da unidade mantidos para futura utilização.', 1, false, 0, 'P');
