insert into contabilidade.planocontadetalhe values (6763, 1, '1.2.2.3.0.00.00.00.00.00', 'INVESTIMENTOS DO RPPS DE LONGO PRAZO', 'Compreende os investimentos realizados pelo Regime Próprio da Previdência Social, em conformidade com a legislação que trata das aplicações e investimentos dos RPPS.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6764, 1, '1.2.2.3.1.00.00.00.00.00', 'INVESTIMENTOS DO RPPS DE LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os investimentos realizados pelo Regime Próprio da Previdência Social, em conformidade com a legislação que trata das aplicações e investimentos dos RPPS. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6765, 1, '1.2.2.3.1.01.00.00.00.00', 'TITULOS E VALORES MOBILIÁRIOS - RPPS', 'Compreende os investimentos realizados pelo Regime Próprio da Previdência Social em títulos e valores mobiliários .', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6766, 1, '1.2.2.3.1.02.00.00.00.00', 'APLICAÇÕES EM SEGMENTO DE IMÓVEIS - RPPS', 'Compreende os investimentos realizados pelo Regime Próprio da Previdência Social em aplicações em segmentos de imóveis.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6767, 1, '1.2.2.7.0.00.00.00.00.00', 'DEMAIS INVESTIMENTOS PERMANENTES', 'Compreende os demais direitos de qualquer natureza não classificáveis no ativo circulante nem no ativo realizável a longo prazo e que não se destinem a manutenção das atividades da entidade.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6768, 1, '1.2.2.7.1.00.00.00.00.00', 'DEMAIS INVESTIMENTOS PERMANENTES - CONSOLIDAÇÃO', 'Compreende os demais direitos de qualquer natureza não classificáveis no ativo circulante nem no ativo realizável a longo prazo e que não se destinem a manutenção das atividades da entidade. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6769, 1, '1.2.3.0.0.00.00.00.00.00', 'IMOBILIZADO', 'Compreende os direitos que tenham por objeto bens corpóreos destinados a manutenção das atividades da entidade ou exercidos com essa finalidade, inclusive os decorrentes de operações que transfiram a ela os benefícios, os riscos e o controle desses bens.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6770, 1, '1.2.3.1.0.00.00.00.00.00', 'BENS MOVEIS', 'Compreende o valor da aquisição ou incorporação de bens corpóreos, que tem existência material e que podem ser transportados por movimento próprio ou removidos por forca alheia sem alteração da substancia ou da destinação economico-social, que constituam meio para a produção de outros bens ou serviços.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6771, 1, '1.2.3.1.1.00.00.00.00.00', 'BENS MOVEIS- CONSOLIDAÇÃO', 'Compreende o valor da aquisição ou incorporação de bens corpóreos, que tem existência material e que podem ser transportados por movimento próprio ou removidos por forca alheia sem alteração da substancia ou da destinação economico-social, que constituam meio para a produção de outros bens ou serviços. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6772, 1, '1.2.3.1.1.01.00.00.00.00', 'MÁQUINAS, APARELHOS, EQUIPAMENTOS E FERRAMENTAS', 'Compreende o valor da aquisição ou incorporação de máquinas, ferramentas, aparelhos, equipamentos, acessórios etc.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6773, 1, '1.2.3.1.1.02.00.00.00.00', 'BENS DE INFORMÁTICA', 'Compreende o valor da aquisição ou incorporação de equipamentos de processamento de dados e sistemas aplicativos, entre outros.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6774, 1, '1.2.3.1.1.03.00.00.00.00', 'MÓVEIS E UTENSÍLIOS', 'Compreende o valor da aquisição ou incorporação de mobiliário em geral e utensílios, entre outros.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6775, 1, '1.2.3.1.1.04.00.00.00.00', 'MATERIAIS CULTURAIS, EDUCACIONAIS E DE COMUNICAÇÃO', 'Compreende o valor da aquisição ou incorporação de materiais bibliográficos, discotecas, filmotecas, de comunicação, entre outros.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6776, 1, '1.2.3.1.1.05.00.00.00.00', 'VEÍCULOS', 'Compreende o valor da aquisição ou incorporação de meios de transportes aéreos, aquáticos e terrestres, entre outros.', 1, false, 0, 'N');
