insert into contabilidade.planocontadetalhe values (6778, 1, '1.2.3.1.1.08.00.00.00.00', 'BENS MÓVEIS EM ALMOXARIFADO', 'Compreende os valores de bens permanentes a serem estocados em almoxarifado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6779, 1, '1.2.3.1.1.99.00.00.00.00', 'DEMAIS BENS MÓVEIS', 'Compreende o valor da aquisição ou incorporação dos demais bens móveis não classificados em grupos específicos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6780, 1, '1.2.3.2.0.00.00.00.00.00', 'BENS IMÓVEIS', 'Compreende o valor dos bens imóveis, os quais são bens vinculados ao solo e que não podem ser retirados sem destruição ou dano, destinados ao uso e que a entidade não esteja explorando comercialmente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6781, 1, '1.2.3.2.1.00.00.00.00.00', 'BENS IMOVEIS- CONSOLIDAÇÃO', 'Compreende o valor dos bens imóveis, os quais são bens vinculados ao solo e que não podem ser retirados sem destruição ou dano, destinados ao uso e que a entidade não esteja explorando comercialmente. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6782, 1, '1.2.3.2.1.01.00.00.00.00', 'BENS DE USO ESPECIAL', 'Compreende os bens, tais como edifícios ou terrenos destinados a serviço ou estabelecimento da administração estadual ou municipal, inclusive os de suas autarquias e fundações públicas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6783, 1, '1.2.3.2.1.04.00.00.00.00', 'BENS DOMINICAIS', 'Compreende os bens que constituem o patrimônio das pessoas jurídicas de direito público, como objeto de direito pessoal, ou real, de cada uma dessas entidades. Compreende ainda, não dispondo a lei em contrário, os bens pertencentes as pessoas jurídicas de direito público a que se tenha dado estrutura de direito privado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6784, 1, '1.2.3.2.1.05.00.00.00.00', 'BENS DE USO COMUM DO POVO', 'Compreende os bens de uso comum do povo construídos ou adquiridos por pessoas jurídicas de direito público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6785, 1, '1.2.4.1.0.00.00.00.00.00', 'SOFTWARES', 'Compreende os valores dos softwares pertencentes à entidade e não integrantes a um hardware, englobando os valores referentes à sua construção, implementação e instalação.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6786, 1, '1.2.4.1.1.00.00.00.00.00', 'SOFTWARES- CONSOLIDAÇÃO', 'Compreende os valores dos softwares pertencentes à entidade e não integrantes a um hardware, englobando os valores referentes à sua construção, implementação e instalação. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6787, 1, '1.2.4.2.0.00.00.00.00.00', 'MARCAS, DIREITOS E PATENTES INDUSTRIAIS', 'Compreende os valores pertinentes a bens intangíveis, englobando os gastos com registro de marca, nome, invenções próprias, direitos de uso de comunicação e direitos autorais, alem de desembolsos a terceiros por contratos de uso de marcas, patentes ou processos de fabricação (tecnologia).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6788, 1, '1.2.4.2.1.00.00.00.00.00', 'MARCAS, DIREITOS E PATENTES INDUSTRIAIS- CONSOLIDAÇÃO', 'Compreende os valores pertinentes a bens intangíveis, englobando os gastos com registro de marca, nome, invenções próprias, direitos de uso de comunicação, direitos autorais e direitos sobre recursos naturais, alem de desembolsos a terceiros por contratos de uso de marcas, patentes ou processos de fabricação (tecnologia). Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6789, 1, '1.2.4.3.0.00.00.00.00.00', 'DIREITO DE USO DE IMÓVEIS', 'Compreende os valores pertinentes a bens intangíveis representados pela aquisição do direito de uso de imóveis.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6790, 1, '1.2.4.3.1.00.00.00.00.00', 'DIREITO DE USO DE IMOVEIS- CONSOLIDAÇÃO', 'Compreende os valores pertinentes a bens intangíveis representados pela aquisição de direito de uso de imóveis. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (6791, 1, '1.2.5.0.0.00.00.00.00.00', 'DIFERIDO', 'Compreende as despesas pré-operacionais e os gastos de reestruturação que contribuirão, efetivamente, para o aumento do resultado de mais de um exercício social e que não configurem tão somente uma redução de custos ou acréscimo na eficiência operacional. Compreende os saldos compreendedos até 2008 e que deverão ser integralmente amortizados até 2017.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6792, 1, '1.2.5.1.0.00.00.00.00.00', 'GASTOS DE IMPLANTAÇÃO E PRÉ-OPERACIONAIS', 'Compreende os gastos com novos empreendimentos, incorridos antes do início de suas operações. Compreende os saldos Compreendedos até 2008 e que deverão ser integralmente amortizados até 2017.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6793, 1, '1.2.5.1.1.00.00.00.00.00', 'GASTOS DE IMPLANTAÇÃO E PRÉ-OPERACIONAIS - CONSOLIDAÇÃO', 'Registra os gastos com novos empreendimentos, incorridos antes do início de suas operações. Compreende os saldos registrados até 2008 e que deverão ser integralmente amortizados até 2017. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (6794, 1, '1.2.5.2.0.00.00.00.00.00', 'GASTOS DE REORGANIZAÇÃO', 'Compreende os gastos com a reorganização, que contribuirão, efetivamente, para o aumento do resultado de mais de um exercício social, registrados até 2008 e que deverão ser integralmente amortizados até 2017.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6795, 1, '1.2.5.2.1.00.00.00.00.00', 'GASTOS DE REORGANIZAÇÃO - CONSOLIDAÇÃO', 'Compreende os gastos com a reorganização, que contribuirão, efetivamente, para o aumento do resultado de mais de um exercício social, registrados até 2008 e que deverão ser integralmente amortizados até 2017. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (6796, 1, '3.9.9.6.0.00.00.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', ' Compreende as variações patrimoniais diminutivas provenientes de indenizações e/ou restituições diversas, não especificadas no plano de contas.', 1, false, 0, 'N');
