insert into contabilidade.planocontadetalhe values (6884, 1, '3.1.9.2.1.00.00.00.00.00', 'PESSOAL REQUISITADO DE OUTROS ORGAOS E ENTES - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas  com pessoal requisitado de outros órgãos e entes.  Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
