insert into contabilidade.planocontadetalhe values (6887, 1, '3.2.0.0.0.00.00.00.00.00', 'BENEFÍCIOS PREVIDENCIÁRIOS E ASSISTENCIAIS', 'Compreendem as variações patrimoniais diminutivas relativas às aposentadorias, pensões, reformas, reserva remunerada e outros benefícios previdenciários de caráter contributivo, do regime próprio da previdência social - RPPS e do regime geral da previdência social - RGPS, bem como as ações de assistência social, que são políticas de seguridade social não contributiva, visando ao enfrentamento da pobreza, a garantia dos mínimos sociais, ao provimento de condições para atender contingencias sociais e a universalização dos direitos sociais.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6888, 1, '3.2.1.0.0.00.00.00.00.00', 'APOSENTADORIAS E REFORMAS', 'Compreendem os benefícios de prestação continuada assegurados pela previdência social com o objetivo de garantir meios indispensáveis de manutenção, por motivo de incapacidade, idade avançada e tempo de serviço.', 1, false, 0, 'N');
