insert into contabilidade.planocontadetalhe values (6911, 1, '3.2.3.9.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS DE PRESTAÇÃO CONTINUADA - CONSOLIDAÇÃO', 'Registra outros benefícios de prestação continuada. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6912, 1, '3.2.4.0.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS', 'Compreendem as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de nascimento, morte, situações de vulnerabilidade temporária e de calamidade pública. As provisões relativas a programas, projetos, serviços e benefícios diretamente vinculados ao campo da saúde, educação, integração nacional e das demais políticas setoriais não se incluem na modalidade de benefícios eventuais da assistência social.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6913, 1, '3.2.4.1.0.00.00.00.00.00', 'AUXÍLIO POR NATALIDADE', 'Compreende o beneficio eventual que visa o pagamento de auxílio por natalidade as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6914, 1, '3.2.4.1.1.00.00.00.00.00', 'AUXÍLIO POR NATALIDADE - CONSOLIDAÇÃO', 'Compreende o beneficio eventual que visa o pagamento de auxílio por natalidade as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
