insert into contabilidade.planocontadetalhe values (6916, 1, '3.2.4.2.1.00.00.00.00.00', 'AUXÍLIO POR MORTE - CONSOLIDAÇÃO', 'Compreende o beneficio eventual que visa o pagamento de auxílio por morte as famílias cuja renda mensal per capita seja inferior a 1/4 (um quarto) do salário mínimo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6917, 1, '3.2.4.3.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS POR SITUAÇÕES DE VULNERABILIDADE TEMPORÁRIA', 'Compreende as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de situações de vulnerabilidade temporária, com prioridade para a criança, a família, o idoso, a pessoa portadora de deficiência, a gestante e a nutriz.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6918, 1, '3.2.4.3.1.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS POR SITUAÇÕES DE VULNERABILIDADE TEMPORÁRIA - CONSOLIDAÇÃO', 'Compreende as provisões suplementares e provisórias, prestadas aos cidadãos e as famílias em virtude de situações de vulnerabilidade temporária, com prioridade para a criança, a família, o idoso, a pessoa portadora de deficiência, a gestante e a nutriz. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6919, 1, '3.2.4.4.0.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS EM CASO DE CALAMIDADE PÚBLICA', 'Compreendem os benefícios para atendimento de vitimas de calamidade pública, de modo a assegurar lhes a sobrevivência e a reconstrução de sua autonomia. Entende-se se por estado de calamidade pública o reconhecimento pelo poder público de situação anormal, advinda de baixas ou altas temperaturas, tempestades, enchentes, inversão térmica, desabamentos, incêndios, epidemias, causando sérios danos à comunidade afetada, inclusive a incolumidade ou a vida de seus integrantes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6920, 1, '3.2.4.4.1.00.00.00.00.00', 'BENEFÍCIOS EVENTUAIS EM CASO DE CALAMIDADE PÚBLICA - CONSOLIDAÇÃO', 'Compreendem os benefícios para atendimento de vitimas de calamidade pública, de modo a assegurar - lhes a sobrevivência e a reconstrução de sua autonomia. Entende-se por estado de calamidade pública o reconhecimento pelo poder público de situação anormal, advinda de baixas ou altas temperaturas, tempestades, enchentes, inversão térmica, desabamentos, incêndios, epidemias, causando sérios danos à comunidade afetada, inclusive a incolumidade ou a vida de seus integrantes. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6921, 1, '3.2.4.9.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS EVENTUAIS', 'Compreende a variação patrimonial diminutiva proveniente de outros benefícios eventuais', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6922, 1, '3.2.4.9.1.00.00.00.00.00', 'OUTROS BENEFÍCIOS EVENTUAIS - CONSOLIDAÇÃO', 'Compreende a variação patrimonial diminutiva proveniente de outros benefícios eventuais.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6923, 1, '3.2.5.0.0.00.00.00.00.00', 'POLÍTICAS PÚBLICAS DE TRANSFERÊNCIA DE RENDA', 'Compreende as políticas públicas que visem contribuir para a redução da fome, da pobreza, da desigualdade e de outras formas de privação vividas pelas famílias mais excluídas, considerando três dimensões: o alivio imediato da pobreza, por meio da transferência de renda diretamente as famílias pobres e extremamente pobres; a contribuição para a redução da pobreza da geração seguinte, por meio do reforço do direito de acesso aos serviços de saúde e de educação, com o cumprimento das condicionalidades nestas áreas; e a articulação de ações complementares, de forma a desenvolver as capacidades das famílias beneficiárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6924, 1, '3.3.2.3.0.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PJ', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa jurídica fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7669, 1, '2.1.2.3.1.01.03.00.00.00', 'FINANCIAMENTO DE CAPITAL DE GIRO', 'Registra os valores exigíveis, decorrentes de financiamentos internos a curto prazo contraídos para o capital de giro.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (6925, 1, '3.5.1.1.0.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA A EXECUÇÃO ORCAMENTARIA', 'Compreende o valor das transferências financeiras concedidas e correspondência de créditos em virtude da execução orçamentária (cota, repassse e sub-repasse). Não Inclui o valor repassado para aportes no RPPS ou RGPS.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6926, 1, '3.2.5.0.1.00.00.00.00.00', 'POLÍTICAS PÚBLICAS DE TRANSFERÊNCIA DE RENDA - CONSOLIDAÇÃO', 'Compreende as políticas públicas que visem contribuir para a redução da fome, da pobreza, da desigualdade e de outras formas de privação vividas pelas famílias mais excluídas, considerando três dimensões: o alivio imediato da pobreza, por meio da transferência de renda diretamente as famílias pobres e extremamente pobres; a contribuição para a redução da pobreza da geração seguinte, por meio do reforço do direito de acesso aos serviços de saúde e de educação, com o cumprimento das condicionalidades nestas áreas; e a articulação de ações complementares, de forma a desenvolver as capacidades das famílias beneficiarias. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6927, 1, '3.2.9.0.0.00.00.00.00.00', 'OUTROS BENEFÍCIOS PREVIDENCIÁRIOS E ASSISTENCIAIS', 'Compreende outras variações patrimoniais diminutivas, relacionadas com benefícios previdenciários ou assistenciais, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
