insert into contabilidade.planocontadetalhe values (6936, 1, '3.3.0.0.0.00.00.00.00.00', 'USO DE BENS, SERVIÇOS E CONSUMO DE CAPITAL FIXO', 'Compreende o somatório das variações patrimoniais diminutivas com manutenção e operação da maquina pública, exceto despesas com pessoal e encargos que serão registradas em grupo específico (despesas de pessoal e encargos). Compreende: diárias, material de consumo, material de distribuição gratuita, passagens e despesas com locomoção, serviços de terceiros, arrendamento mercantil operacional, aluguel, depreciação, amortização, exaustão, entre outras.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6937, 1, '3.3.1.0.0.00.00.00.00.00', 'USO DE MATERIAL DE CONSUMO', 'Compreende as variações patrimoniais diminutivas provenientes da distribuição do material de consumo. Um material é considerado de consumo quando for de duração inferior a dois anos, frágil, perecível, incorporável, transformável ou cuja finalidade seja para consumo imediato ou para reposição.', 1, false, 0, 'N');
