insert into contabilidade.planocontadetalhe values (7082, 1, '7.3.1.1.2.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA  NÃO-TRIBUTÁRIA', 'Compreende o montante dos créditos a encaminhar e encaminhados para inscrição em Dívida Ativa Não-Tributária..', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6945, 1, '3.3.2.2.0.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PF', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa física fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6946, 1, '3.3.2.2.1.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PF - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa física fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6947, 1, '3.3.2.3.1.00.00.00.00.00', 'SERVIÇOS TERCEIROS - PJ - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes da prestação de serviços por pessoa jurídica fornecida a entidade governamental. Na classificação da despesa de material por encomenda, a variação patrimonial diminutiva só devera ser classificada com serviços de terceiros se o próprio órgão ou entidade fornecer a matéria-prima. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6948, 1, '3.3.2.4.0.00.00.00.00.00', 'CONTRATO DE TERCEIRIZAÇÃO POR SUBSTITUIÇÃO DE MÃO DE OBRA - ART. 18 § 1, LC 101/00', 'Compreende a apropriação das despesas relativas à mão de obra, constantes dos contratos de terceirização, classificáveis no grupo de despesa com pessoal e encargos em obediência ao disposto no artigo 18, § 1º, da lei complementar nº 101, de 2000.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6949, 1, '3.3.2.4.1.00.00.00.00.00', 'CONTRATO DE TERCEIRIZAÇÃO POR SUBSTITUIÇÃO DE MÃO DE OBRA - ART. 18 § 1, LC 101/00 - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas relativas à mão de obra, constantes dos contratos de terceirização, classificáveis no grupo de despesa com pessoal e encargos em obediência ao disposto no artigo 18, § 1º, da lei complementar nº 101, de 2000. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
