insert into contabilidade.planocontadetalhe values (6954, 1, '3.3.3.2.0.00.00.00.00.00', 'AMORTIZAÇÃO', 'Compreende a redução do valor aplicado na aquisição de direitos de propriedade e quaisquer outros, inclusive ativos intangíveis, com existência ou exercício de duração limitada, ou cujo objeto sejam bens de utilização por prazo legal ou contratualmente limitado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (6955, 1, '3.5.1.2.2.02.00.00.00.00', 'TRANSFERÊNCIAS  NÃO FINANCEIRAS CONCEDIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Compreende o valor das transferências concedidas de bens ou valores independentes da execução orçamentária.', 1, false, 0, 'N');
