insert into contabilidade.planocontadetalhe values (7020, 1, '8.1.1.1.1.01.04.00.00.00', 'FIANÇAS EXECUTADAS', 'Registra o valor recebido das garantias por fianças, no país.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (7021, 1, '3.4.3.9.5.00.00.00.00.00', 'OUTRAS VARIAÇÕES MONETÁRIAS E CAMBIAIS - INTER OFSS - MUNICÍPIO', 'Compreende a variação patrimonial diminutiva proveniente de variações da nossa própria moeda em relação aos índices ou coeficientes aplicáveis por dispositivo legal ou contratual e a variação do valor da nossa moeda em relação às moedas estrangeiras, com exceção a dívida contratual e mobiliaria. Ressalte-se que será tratada como variação monetária apenas a correção monetária pós-fixada. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7022, 1, '3.4.4.0.0.00.00.00.00.00', 'DESCONTOS FINANCEIROS CONCEDIDOS', 'Compreende o valor da variação patrimonial diminutiva com descontos financeiros concedidos a clientes por pagamentos antecipados de duplicatas e outros títulos. Não se confundem com descontos nos preços de venda concedidos incondicionalmente, ou abatimentos de preços, que são deduções da receita.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7023, 1, '3.4.4.0.1.00.00.00.00.00', 'DESCONTOS FINANCEIROS CONCEDIDOS  - CONSOLIDAÇÃO', 'Compreende o valor da variação patrimonial diminutiva com descontos financeiros concedidos a clientes por pagamentos antecipados de duplicatas e outros títulos. Não se confundem com descontos nos preços de venda concedidos incondicionalmente, ou abatimentos de preços, que são deduções da receita. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7024, 1, '3.4.8.0.0.00.00.00.00.00', 'APORTES AO BANCO CENTRAL', 'Compreende os aportes de recursos do Tesouro Nacional ao Banco Central do Brasil. Conta de uso exclusivo da União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7025, 1, '3.4.8.1.0.00.00.00.00.00', 'RESULTADO NEGATIVO DO BANCO CENTRAL', 'Compreende os aportes de títulos públicos para cobertura de resultado negativo do Banco Central do Brasil, conforme art. 7º da Lei Complementar 101, de 4/5/2000. ', 1, false, 0, 'N');
