insert into contabilidade.planocontadetalhe values (7027, 1, '3.4.8.2.0.00.00.00.00.00', 'MANUTENÇÃO DA CARTEIRA DE TÍTULOS', 'Compreende os aportes de títulos públicos ao Banco Central do Brasil, para complementação de carteira, conforme disposto na Lei nº 10.179, de 6/2/2011.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7028, 1, '3.4.8.2.1.00.00.00.00.00', 'MANUTENÇÃO DA CARTEIRA DE TÍTULOS - CONSOLIDAÇÃO', 'Compreende os aportes de títulos públicos ao Banco Central do Brasil, para complementação de carteira, conforme disposto na Lei nº 10.179, de 6/2/2011. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7029, 1, '3.4.9.0.0.00.00.00.00.00', 'OUTRAS VARIAÇÕES PATRIMONIAIS DIMINUTIVAS - FINANCEIRAS', 'Compreende outras variações patrimoniais diminutivas financeiras, não abrangidas nos grupos anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7030, 1, '3.4.9.1.0.00.00.00.00.00', 'JUROS E ENCARGOS EM SENTENÇAS JUDICIAIS', 'Compreende os juros e encargos resultantes de pagamento de precatórios e de sentenças judiciais transitadas em julgado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7031, 1, '3.4.9.1.1.00.00.00.00.00', 'JUROS E ENCARGOS EM SENTENÇAS JUDICIAIS - CONSOLIDAÇÃO', 'Compreende os juros e encargos resultantes de pagamento de precatórios e de sentenças judiciais transitadas em julgado. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7032, 1, '3.4.9.2.0.00.00.00.00.00', 'JUROS E ENCARGOS EM INDENIZAÇÕES E RESTITUIÇÕES', 'Compreende as variações patrimoniais diminutivas com indenizações e restituições referentes a juros e encargos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7033, 1, '3.4.9.2.1.00.00.00.00.00', 'JUROS E ENCARGOS EM INDENIZAÇÕES E RESTITUIÇÕES - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas com indenizações e restituições referentes a juros e encargos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7034, 1, '3.4.9.9.0.00.00.00.00.00', 'OUTRAS VARIAÇÕES PATRIMONIAIS DIMINUTIVAS FINANCEIRAS', 'Compreende as variações patrimoniais diminutivas provenientes de operações financeiras não compreendidas nas contas anteriores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7035, 1, '3.4.9.9.1.00.00.00.00.00', 'OUTRAS VARIAÇÕES PATRIMONIAIS DIMINUTIVAS FINANCEIRAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas provenientes de operações financeiras não compreendidas nas contas anteriores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7036, 1, '3.5.0.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS E DELEGAÇÕES CONCEDIDAS', 'Compreende o somatório das variações patrimoniais diminutivas com transferências inter governamentais, transferências intragovernamentais, transferências a instituições multigovernamentais, transferências a instituições privadas com ou sem fins lucrativos, transferências a convênios, transferências ao exterior e execuções orçamentárias delegadas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7037, 1, '8.1.1.1.1.02.05.00.00.00', 'HIPOTECAS A EXECUTAR', 'Registra o valor das garantias por hipoteca pendentes de execução, no exterior.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (7038, 1, '3.5.1.1.2.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA A EXECUÇÃO ORÇAMENTÁRIA -  INTRA OFSS', 'Compreende o valor das transferências financeiras concedidas e correspondência de créditos em virtude da execução orçamentária (cota, repassse e sub-repasse). Não Inclui o valor repassado para aportes no RPPS ou RGPS.Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7039, 1, '3.5.1.2.0.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Compreende o valor das transferências financeiras, de bens ou valores, concedidas para restos a pagar e outras finalidades independentes da execução orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7040, 1, '3.5.1.2.2.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA - INTRA OFSS', 'Compreende o valor das transferências financeiras, de bens ou valores, concedidas para restos a pagar e outras finalidades independentes da execução orçamentária. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7041, 1, '3.5.1.2.2.01.00.00.00.00', 'TRANSFERÊNCIAS FINANCEIRAS CONCEDIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Compreende o valor das transferências financeiras concedidas para restos a pagar e outras finalidades independentes da execução orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7042, 1, '2.1.8.9.1.09.01.00.00.00', 'TERMOS DE PARCERIA A PAGAR DO EXERCÍCIO', 'Registra os valores a pagar referentes a termos de parceria firmados no exercício.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7043, 1, '3.5.6.0.1.00.00.00.00.00', 'TRANSFERÊNCIAS AO EXTERIOR - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências a organismos e fundos internacionais, de governos estrangeiros e instituições privadas com ou sem fins lucrativos no exterior. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com unidade que não pertença ao OFSS de ente público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7044, 1, '3.5.1.3.2.00.00.00.00.00', 'TRANSFERENCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O RPPS - INTRA OFSS', 'Compreende o valor das transferências para aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva, e cobertura de déficits financeiros ou atuariais do RPPS e outros aportes exceto os decorrentes de alíquota de contribuição suplementar. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7045, 1, '3.5.1.3.2.01.00.00.00.00', 'PLANO FINANCEIRO', 'Compreende o valor total dos valores concedidos pelo RPPS para cobertura de insuficiências financeiras, formação de reserva e outras necessidades financeiras, no âmbito do plano financeiro.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7046, 1, '3.5.1.3.2.02.00.00.00.00', 'PLANO PREVIDENCIÁRIO', 'Compreende o valor total concedido ao RPPS para cobertura de déficit financeiro ou atuarial e outros aportes, no âmbito do plano previdenciário.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7047, 1, '3.5.1.4.0.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O RGPS', 'Compreende o valor das transferências para os aportes financeiros da União para a cobertura de déficits financeiros ou atuariais do RGPS.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7048, 1, '3.5.1.4.2.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O RGPS  - INTRA OFSS', 'Compreende o valor das transferências para os aportes financeiros da União para a cobertura de déficits financeiros ou atuariais do RGPS. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7049, 1, '3.5.1.4.2.01.00.00.00.00', 'RECURSOS PARA COBERTURA DE INSUFICIÊNCIAS FINANCEIRAS', 'Compreende os recursos orçamentários efetivamente utilizados para a cobertura de insuficiências financeiras entre as receitas e despesas previdenciáriasno exercício de referência.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7050, 1, '3.5.1.5.0.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O SISTEMA DE PAGAMENTO DE PENSÕES MILITARES', 'Compreende o valor das transferências para os aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva e cobertura de déficits financeiros ou atuariais do regime dos militares e outros aportes, exceto os decorrentes de alíquota de contribuição suplementar.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7051, 1, '3.5.1.5.2.00.00.00.00.00', 'TRANSFERÊNCIAS CONCEDIDAS PARA APORTES DE RECURSOS PARA O SISTEMA DE PAGAMENTO DE PENSÕES MILITARES  - INTRA OFSS', 'Compreende o valor das transferências para os aportes financeiros do ente para cobertura de insuficiências financeiras, formação de reserva e cobertura de déficits financeiros ou atuariais do regime dos militares e outros aportes, exceto os decorrentes de alíquota de contribuição suplementar. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7052, 1, '3.5.2.0.0.00.00.00.00.00', 'TRANSFERÊNCIAS INTER GOVERNAMENTAIS', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7053, 1, '3.5.2.1.0.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7054, 1, '3.5.2.1.1.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente. (a empresa independente não pertence ao OFSS mas pertence ao conceito de governamental).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7055, 1, '3.5.2.1.3.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - UNIÃO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor.Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) de entes públicos distintos, para permitir a consolidação nacional realizada pelo Poder Executivo da União.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7056, 1, '3.5.2.1.4.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - ESTADO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na Constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7057, 1, '3.5.2.1.5.00.00.00.00.00', 'DISTRIBUIÇÃO CONSTITUCIONAL OU LEGAL DE RECEITAS - INTER OFSS - MUNICÍPIO', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência a outras esferas de governo de receitas tributárias, de contribuições e de outras receitas vinculadas, prevista na constituição ou em leis específicas, cuja competência de arrecadação é do órgão transferidor. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7058, 1, '3.5.2.2.0.00.00.00.00.00', 'TRANSFERÊNCIAS AO FUNDEB', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência de recursos dos entes, incluindo a complementação da União, para a composição do Fundo de Manutenção e Desenvolvimento da Educação Básica e de Valorização dos Profissionais da Educação, instituído pela E.C. n.º 53/2006 e regulamentado pela lei 11.494/2007.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7059, 1, '3.9.3.0.0.00.00.00.00.00', 'OPERAÇÕES DA AUTORIDADE MONETÁRIA', 'Compreende as variações patrimoniais diminutivas do Banco Central do Brasil, na qualidade de autoridade monetária. Conta de uso exclusivo da União. ', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7060, 1, '3.5.2.2.4.00.00.00.00.00', 'TRANSFERÊNCIAS AO FUNDEB - INTER-OFSS - ESTADO ', 'Compreende as variações patrimoniais diminutivas decorrentes da transferência de recursos dos entes,incluindo a complementação da União, para a composição do Fundo de Manutenção e Desenvolvimento da Educação Básica e de Valorização dos Profissionais da Educação, instituído pela E.C. n.º 53/2006 e regulamentado pela lei 11.494/2007. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7061, 1, '3.5.2.3.0.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, bem como a demais entidades, de bens e/ou valores.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7062, 1, '3.5.2.3.1.00.00.00.00.00', 'TRANSFERÊNCIAS VOLUNTÁRIAS - CONSOLIDAÇÃO', 'Compreende as variações patrimoniais diminutivas decorrentes de transferências voluntárias a União, estados, distrito federal, municípios, inclusive as entidades vinculadas, de bens e/ou valores. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS). Por exemplo, empresa independente de um ente recebendo transferência de outro ente. (A empresa independente não pertence ao OFSS, mas pertence ao conceito de governamental).', 1, false, 0, 'N');
