insert into contabilidade.planocontadetalhe values (7117, 1, '3.6.1.7.0.00.00.00.00.00', 'VARIAÇÃO PATRIMONIAL DIMINUTIVA COM AJUSTE DE PERDAS DE CRÉDITOS E DE INVESTIMENTOS E APLICAÇÕES TEMPORÁRIOS', 'Compreende a variação patrimonial diminutiva com ajuste de perdas de créditos por inadimplência ou outros fatores que impossibilitam o recebimento de créditos. Por exemplo, a variação patrimonial diminutiva com ajuste para créditos de liquidação duvidosa e ajuste para créditos da dívida ativa. Compreende também a variação patrimonial diminutiva com ajuste de perdas em investimentos e aplicações temporários, não destinados à negociação e que não façam parte das atividades operacionais da entidade, resgatáveis no curto e no longo prazo.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7136, 1, '3.6.2.2.0.00.00.00.00.00', 'PERDAS COM ALIENAÇÃO DE IMOBILIZADO', 'Compreende a perda com alienação de ativos imobilizados, ou seja, quando o valor alienado do ativo e menor que o seu valor contábil, de maneira que a diferença compreende a perda.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7118, 1, '3.6.1.7.1.00.00.00.00.00', 'VARIAÇÃO PATRIMONIAL DIMINUTIVA COM AJUSTE DE PERDAS DE CRÉDITOS  E DE INVESTIMENTOS E APLICAÇÕES TEMPORÁRIOS- CONSOLIDAÇÃO', 'Compreende a variação patrimonial diminutiva com ajuste de perdas de créditos por inadimplência ou outros fatores que impossibilitam o recebimento de créditos. Compreende também a variação patrimonial diminutiva com ajuste de perdas em investimentos e aplicações temporários, não destinados à negociação e que não façam parte das atividades operacionais da entidade, resgatáveis no curto e no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
