insert into contabilidade.planocontadetalhe values (7160, 1, '3.7.0.0.0.00.00.00.00.00', 'TRIBUTÁRIAS', 'Compreendem as variações patrimoniais diminutivas relativas aos impostos, taxas, contribuições de melhoria, contribuições sociais, contribuições econômicas e contribuições especiais.', 1, false, 0, 'N');
