insert into contabilidade.planocontadetalhe values (7323, 1, '5.2.2.1.1.02.00.00.00.00', 'CREDITOS ANTECIPADOS - LDO ', 'Compreende os valores relativos as antecipações de créditos determinados pela lei de diretrizes orçamentarias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7324, 1, '5.2.2.1.2.00.00.00.00.00', 'DOTAÇÃO ADICIONAL POR TIPO DE CREDITO', 'Compreende o somatório dos valores monetários da dotação orçamentária decorrentes da abertura de créditos adicionais e seus cancelamentos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7326, 1, '5.2.2.1.2.03.00.00.00.00', 'CREDITO ADICIONAL - EXTRAORDINÁRIO', 'Compreende o somatório dos valores referentes à abertura de créditos adicionais extraordinários destinados a despesas imprevisíveis e urgentes como as decorrentes de guerra, comoção interna ou calamidade pública.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7327, 1, '5.2.2.1.3.00.00.00.00.00', 'DOTAÇÃO ADICIONAL POR FONTE', ' Compreende o somatório dos valores da dotação adicional por fonte de recursos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7328, 1, '5.2.2.1.9.01.00.00.00.00', 'ALTERAÇÃO DO QUADRO DE DETALHAMENTO DA DESPESA ', 'Compreende a movimentação dos créditos por acréscimo ou redução decorrente de alteração do quadro de detalhamento de despesas - QDD (elemento de despesa).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7329, 1, '5.2.2.1.9.02.00.00.00.00', 'ALTERAÇÃO DA LEI ORCAMENTARIA ', 'Compreende as alterações na lei orçamentária, inclusive as decorrentes de transposição, remanejamento e transferência de dotações orçamentárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7330, 1, '5.2.2.2.0.00.00.00.00.00', 'MOVIMENTAÇÃO DE CRÉDITOS RECEBIDOS', 'Compreende o somatório dos valores dos recebimentos de créditos orçamentários através de destaque, provisão ou por fusão, cisão ou extinção de unidades.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7331, 1, '5.2.2.2.1.00.00.00.00.00', 'DESCENTRALIZAÇÃO INTERNA DE CRÉDITOS - PROVISÃO', 'Compreende os valores dos créditos descentralizados das unidades orçamentárias para as unidades administrativas sob sua jurisdição ou entre estas, no âmbito do próprio ministério, secretaria ou órgão equivalente, recebidos ao longo do exercício.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7332, 1, '5.2.2.2.2.00.00.00.00.00', 'DESCENTRALIZAÇÃO EXTERNA DE CRÉDITOS - DESTAQUE', 'Compreende a descentralização do credito inicial ou adicional recebido de unidades ou órgãos pertencentes a ministérios, secretarias e órgãos equivalentes distintos ou entre estes e entidades da administração indireta.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7333, 1, '5.2.2.2.2.01.00.00.00.00', 'ADMINISTRAÇÃO DIRETA                ', 'Compreende o valor referente a movimentação de créditos decorrentes de recebimento ou de transferência de outros órgãos e entidades de igual ou de diferente unidade orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7334, 1, '5.2.2.2.2.02.00.00.00.00', 'ADMINISTRAÇÃO INDIRETA                 ', 'Compreende o valor referente a movimentação de créditos decorrentes de recebimento ou de transferência de outros órgãos e entidades de igual ou de diferente unidade orçamentária.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7335, 1, '5.2.2.2.2.09.00.00.00.00', 'ALTERAÇÃO DE CREDITOS MOVIMENTADOS     ', 'Compreende o valor da alteração de créditos orçamentários por acréscimo ou redução do quadro de detalhamento de despesa - QDD, por UG não pertencente ao órgão ou entidade da unidade.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7336, 1, '5.2.2.2.9.00.00.00.00.00', 'OUTRAS DESCENTRALIZAÇÕES DE CRÉDITOS', 'Compreende as movimentações de créditos recebidos por cisão, fusão, extinção, compensação e outras transações.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7337, 1, '5.2.2.3.0.00.00.00.00.00', 'DETALHAMENTO DE CREDITO', 'Compreende o somatório relativos às dotações detalhadas relativas a cada ação orçamentária (projeto, atividade ou operações especiais).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7338, 1, '5.2.2.9.0.00.00.00.00.00', 'OUTROS CONTROLES DA DESPESA ORÇAMENTÁRIA', 'Compreende o somatório dos valores das despesas pré-empenhadas e empenhadas por detalhamento específico.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7339, 1, '5.2.2.9.1.00.00.00.00.00', 'PRÉ-EMPENHOS DA DESPESA   ', 'Compreende o credito orçamentário pré-empenhado, para atender objetivo especifico, nos casos em que a despesa a ser realizada, por suas características, exige um intervalo de tempo desde a decisão da realização da despesa até a efetivação da emissão de NE.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7340, 1, '5.2.2.9.2.00.00.00.00.00', 'EMPENHOS POR EMISSÃO', 'Compreendeo somatório dos valores de empenho emitido por ato emanado de autoridade competente que cria para o poder público a obrigação de pagar as despesas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7341, 1, '5.2.2.9.2.01.00.00.00.00', 'EXECUÇÃO DA DESPESA POR NOTA DE EMPENHO', 'Compreende os valores executados da despesa, detalhados por nota de empenho.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7342, 1, '5.3.0.0.0.00.00.00.00.00', 'INSCRIÇÃO DE RESTOS A PAGAR', 'Compreende o somatório relativo ao valor da inscrição das despesas empenhadas e não pagas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7343, 1, '5.3.1.0.0.00.00.00.00.00', 'INSCRIÇÃO DE RP NÃO PROCESSADOS', 'Compreende o somatório dos valores inscritos em restos a pagar não-processados relativos às despesas empenhadas e não liquidadas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7344, 1, '7.0.0.0.0.00.00.00.00.00', 'CONTROLES DEVEDORES', 'Compreende as contas em que são registrados atos potenciais e controles específicos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7345, 1, '7.1.0.0.0.00.00.00.00.00', 'ATOS POTENCIAIS', 'Compreende contas relacionadas às situações não compreendidas no patrimônio, mas que, direta ou indiretamente, possam vir à afeta-lo, exclusive as que dizem respeito a atos e fatos ligados a execução orçamentária e financeira e as contas com função precípua de controle.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7346, 1, '7.1.1.0.0.00.00.00.00.00', 'ATOS POTENCIAIS ATIVOS', 'Compreende os atos e fatos que possam vir a aumentar o ativo da entidade governamental.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7347, 1, '7.1.1.1.0.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS RECEBIDAS', 'Compreende contas relacionadas ao registro de avais, fianças e hipotecas recebidas no país e no exterior, bem como emolumentos consulares e outras garantias e contragarantias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7348, 1, '7.1.1.1.1.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS RECEBIDAS - CONSOLIDAÇÃO', 'Compreende o valor recebido de compromisso de adimplência de obrigação financeira ou contratual, no país. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7349, 1, '7.1.1.1.1.01.00.00.00.00', 'GARANTIAS RECEBIDAS NO PAÍS', 'Compreende o valor recebido de compromisso de adimplência de obrigação financeira ou contratual, no país.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7350, 1, '7.1.1.1.1.02.00.00.00.00', 'GARANTIAS RECEBIDAS NO EXTERIOR', 'Compreende o valor recebido de compromisso de adimplência de obrigação financeira ou contratual, no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7351, 1, '7.1.1.1.1.03.00.00.00.00', 'CONTRAGARANTIAS RECEBIDAS NO PAÍS', 'Compreende o valor das contragarantias recebidas referentes as concessões de garantias concedidas sobre obrigações contraídas no país.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7352, 1, '7.1.1.1.1.04.00.00.00.00', 'CONTRAGARANTIAS RECEBIDAS NO EXTERIOR', 'Compreende o valor das contragarantias recebidas referentes as concessões de garantias concedidas sobre obrigações contraídas no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7353, 1, '7.1.1.2.0.00.00.00.00.00', 'DIREITOS CONVENIADOS E OUTROS INSTRUMENTOS CONGÊNERES', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades, objetivando o cumprimento de finalidades comuns.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7354, 1, '7.3.2.0.0.00.00.00.00.00', 'CONTROLE DA INSCRIÇÃO DE CRÉDITOS EM DÍVIDA ATIVA', 'Compreende as contas que controlam os créditos passiveis de serem inscritos em dívida ativa pelo órgão responsável pela inscrição em dívida ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7355, 1, '7.1.1.2.1.00.00.00.00.00', 'DIREITOS CONVENIADOS E OUTROS INSTRUMENTOS CONGÊNERES - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades objetivando o cumprimento de finalidades comuns. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7356, 1, '7.1.1.3.0.00.00.00.00.00', 'DIREITOS CONTRATUAIS', 'Compreende contas relacionadas ao registro dos valores de direitos contratuais, quando a administração pública participa como contratada.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7357, 1, '7.1.1.3.1.00.00.00.00.00', 'DIREITOS CONTRATUAIS - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro dos valores de direitos contratuais, quando a administração pública participa como contratada. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7358, 1, '7.1.1.9.0.00.00.00.00.00', 'OUTROS ATOS POTENCIAIS ATIVOS', 'Compreende os atos potenciais ativos não contemplados em contas especificas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7359, 1, '7.1.2.0.0.00.00.00.00.00', 'ATOS POTENCIAIS PASSIVOS', 'Compreende os atos e fatos que possam vir a aumentar o passivo ou diminuir o ativo da entidade governamental.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7360, 1, '7.1.2.1.0.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS CONCEDIDAS', 'Compreende contas relacionadas ao registro de avais, fianças e hipotecas concedidas no país e no exterior, bem como emolumentos consulares e outras garantias e contragarantias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7361, 1, '7.1.2.1.1.00.00.00.00.00', 'GARANTIAS E CONTRAGARANTIAS CONCEDIDAS - CONSOLIDAÇÃO', 'Compreende o valor concedido de compromisso de adimplência de obrigação financeira ou contratual, no país. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7362, 1, '7.1.2.1.1.01.00.00.00.00', 'GARANTIAS CONCEDIDAS NO PAÍS', 'Compreende o valor concedido de compromisso de adimplência de obrigação financeira ou contratual, no país.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7363, 1, '7.1.2.1.1.02.00.00.00.00', 'GARANTIAS CONCEDIDAS NO EXTERIOR', 'Compreende o valor concedido de compromisso de adimplência de obrigação financeira ou contratual, no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7364, 1, '7.1.2.1.1.03.00.00.00.00', 'CONTRAGARANTIAS CONCEDIDAS NO PAÍS', 'Compreende o valor das contragarantias concedidas referentes as concessões de garantias concedidas sobre obrigações contraídas no pais.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7365, 1, '7.1.2.1.1.04.00.00.00.00', 'CONTRAGARANTIAS CONCEDIDAS NO EXTERIOR', 'Compreende o valor das contragarantias concedidas referentes as concessões de garantias concedidas sobre obrigações contraídas no exterior.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7366, 1, '7.1.2.2.0.00.00.00.00.00', 'OBRIGAÇÕES CONVENIADAS E OUTROS INSTRUMENTOS CONGÊNERES', 'Compreende contas relacionadas ao registro, no concedente, do valor dos convênios firmados entre entidades, objetivando o cumprimento de finalidades comuns.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7367, 1, '7.1.2.2.1.00.00.00.00.00', 'OBRIGAÇÕES CONVENIADAS E OUTROS INSTRUMENTOS CONGÊNERES - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro, no convenente, dos convênios firmados entre entidades objetivando o cumprimento de finalidades comuns. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7368, 1, '7.1.2.3.0.00.00.00.00.00', 'OBRIGAÇÕES CONTRATUAIS', 'Compreende contas relacionadas ao registro dos valores de obrigações contratuais, quando a administração pública participa como contratante.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7369, 1, '7.1.2.3.1.00.00.00.00.00', 'OBRIGAÇÕES CONTRATUAIS - CONSOLIDAÇÃO', 'Compreende contas relacionadas ao registro dos valores de obrigações contratuais, quando a administração pública participa como contratante. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7370, 1, '7.1.2.3.1.08.00.00.00.00', 'CONTRATOS DE PPP', 'Compreende o valor dos passivos decorrentes de contrato de PPP, incluindo o valor das obrigações contraturais com as contraprestações futuras, assim como os riscos não provisionados e outros passivos contingentes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7371, 1, '7.1.2.9.0.00.00.00.00.00', 'OUTROS ATOS POTENCIAIS PASSIVOS', 'Compreende os atos potenciais passivos não contemplados em contas especificas.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7372, 1, '7.2.0.0.0.00.00.00.00.00', 'ADMINISTRAÇÃO FINANCEIRA', 'Compreende as contas de registro da programação financeira e de controle das disponibilidades.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7373, 1, '7.2.1.0.0.00.00.00.00.00', 'DISPONIBILIDADES POR DESTINAÇÃO', 'Compreende as contas que registram os controles sobre as disponibilidades por destinação de recursos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7374, 1, '7.2.1.1.0.00.00.00.00.00', 'CONTROLE DA DISPONIBILIDADE DE RECURSOS', 'Compreende as contas que registram as disponibilidades de recursos recebidos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7375, 1, '7.2.1.2.0.00.00.00.00.00', 'LIMITE DE RESTOS A PAGAR POR DESTINAÇÃO', 'Compreende as contas que controlam a inscrição do limite de restos a pagar, classificados por destinação de recursos', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7376, 1, '7.2.1.3.0.00.00.00.00.00', 'RECURSO DIFERIDO POR DESTINAÇÃO', 'Compreende as contas que controlam a inscrição dos recursos diferidos. Recursos diferidos são as sobras de recursos financeiros ao final do exercício (recurso diferido), que deveriam ser devolvidas a conta única, porem, ao invés disso, essas sobras são liberadas para uso (apenas no exercício seguinte) através de descontos das transferências financeiras (cota, repasse e sub-repasse) do exercício seguinte.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7377, 1, '7.2.2.0.0.00.00.00.00.00', 'PROGRAMAÇÃO FINANCEIRA', 'Compreende as contas que registram as cotas, repasses e sub-repasses para atender a despesas orçamentárias, restos a pagar autorizados e demais dispêndios extra orçamentários.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7378, 1, '7.2.3.0.0.00.00.00.00.00', 'INSCRIÇÃO DO LIMITE ORÇAMENTÁRIO', 'Compreende as contas que controlam o limite de empenho e movimentação de créditos orçamentários, conforme estabelecidos em ato próprio dos poderes e ministério público.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7379, 1, '7.2.4.0.0.00.00.00.00.00', 'CONTROLES DA ARRECADAÇÃO', 'Compreende as contas que registram o valor da arrecadação liquida da receita orçamentária, por meio de documentos de arrecadação como DARF, GRU, DAR, entre outros.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7466, 1, '1.1.4.9.1.02.00.00.00.00', '(-) AJUSTE DE PERDAS ESTIMADAS COM APLICAÇÕES TEMPORÁRIAS EM METAIS PRECIOSOS', 'Registra o ajuste de perdas estimadas, referente a aplicações temporárias em metais preciosos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7380, 1, '7.3.0.0.0.00.00.00.00.00', 'DÍVIDA ATIVA', 'Compreende o controle dos créditos a serem inscritos em dívida ativa, dos que se encontram em processamento. Compreende as contas que controlam os créditos passiveis de serem encaminhados e inscritos em dívida ativa o de inscrição e a tramitação dos créditos inscritos.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7381, 1, '7.3.1.0.0.00.00.00.00.00', 'CONTROLE DO ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA', 'Compreende os valores passiveis de serem encaminhados e inscritos em dívida ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7382, 1, '7.3.1.1.0.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA  ', 'Compreende o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7383, 1, '7.3.1.1.1.00.00.00.00.00', 'ENCAMINHAMENTO DE CRÉDITOS PARA INSCRIÇÃO EM DÍVIDA ATIVA TRIBUTÁRIA ', 'Compreende o montante dos créditos passíveis de serem encaminhados para inscrição em Dívida Ativa Tributária..', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7384, 1, '7.4.0.0.0.00.00.00.00.00', 'RISCOS FISCAIS', 'Compreende as contas que controlam os riscos fiscais que não preencham os requisitos para reconhecimento como passivo, conforme identificados no anexo de riscos fiscais da lei de diretrizes orçamentárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7385, 1, '7.4.1.0.0.00.00.00.00.00', 'CONTROLE DE PASSIVOS CONTINGENTES', 'Compreende as contas que registram o controle dos riscos fiscais que não preencham os requisitos para reconhecimento como passivo classificados como passivos contingentes, conforme identificados no anexo de riscos fiscais da lei de diretrizes orçamentárias.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7386, 1, '7.4.1.1.0.00.00.00.00.00', 'PASSIVOS CONTINGENTES', 'Compreende os riscos fiscais classificados como Passivos Contingentes.', 1, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (7387, 1, '7.4.2.0.0.00.00.00.00.00', 'CONTROLE DOS DEMAIS RISCOS FISCAIS', 'Compreende as contas que registram os riscos fiscais que não preencham os requisitos para reconhecimento como passivo, não classificados como passivos contingentes, conforme identificados no anexo de riscos fiscais da lei de diretrizes orçamentárias.', 1, false, 0, 'N');
