insert into contabilidade.planocontadetalhe values (7839, 1, '2.1.7.5.5.01.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DA COTA DE ICMS ', 'Registra o valor da provisão para repartição da cota de ICMS reconhecida no lançamento por parte do agente arrecadador, a se repartido com os municípios. Na arrecadação, esta provisão será revertida em conta de passivo referente a obrigações de repartição a municípios.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7840, 1, '2.1.7.5.5.02.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DA COTA DE IPVA', 'Registra o valor da provisão para repartição da cota de IPVA reconhecida no lançamento por parte do agente arrecadador, a se repartido com os municípios. Na arrecadação, esta provisão será revertida em conta de passivo referente a obrigações de repartição a municípios.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7841, 1, '2.1.7.5.5.03.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DA COTA-PARTE DO IPI', 'Registra o valor da provisão para repartição da cota parte do IPI reconhecida no lançamento por parte do agente arrecadador, a se repartido com os municípios. Na arrecadação, esta provisão será revertida em conta de passivo referente a obrigações de repartição a municípios.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7842, 1, '2.1.7.5.5.99.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DA COTA DE OUTROS TRIBUTOS/RECEITAS', 'Registra o valor da provisão para repartição da cota parte de outros tributos reconhecida no lançamento por parte do agente arrecadador, a se repartido com os municípios. Na arrecadação, esta provisão será revertida em conta de passivo referente a obrigações de repartição a municípios.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7843, 1, '2.1.7.9.1.01.00.00.00.00', 'PROVISÃO PARA GARANTIAS', 'Registra os valores das provisões destinadas a cobrir despesas com a prestação de garantia relativas a produtos vendidos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7844, 1, '2.1.7.9.1.02.00.00.00.00', 'PROVISÃO PARA REESTRUTURAÇÃO', 'Registra os valores das provisões destinadas a cobrir obrigações oriundas da venda ou extinção de uma linha de negócios, fechamento de fábricas ou locais de negócios, mudanças na estrututa da administração e reorganizações com efeito relevante na natureza e foco das operações da entidade.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7845, 1, '2.1.8.3.1.01.00.00.00.00', 'ARRENDAMENTO OPERACIONAL A PAGAR', 'Registra as obrigações a curto prazo referentes a arrendamentos mercantis, nos quais não são transferidos ao arrendador os riscos e benefícios inerentes a propriedade, não havendo a possibilidade de opção de compra do bem arrendado.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7846, 1, '2.1.8.4.1.01.00.00.00.00', 'DEBENTURES CONVERSIVEIS EM AÇÕES NO CURTO PRAZO', 'Registra os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do título e que são conversíveis em ações, com vencimento até o termino do exercício seguinte.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7847, 1, '2.1.8.4.1.02.00.00.00.00', 'DEBENTURES NAO-CONVERSIVEIS EM AÇÕES NO CURTO PRAZO', 'Registra os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do título e que não são conversíveis em ações, com vencimento até o termino do exercício seguinte.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7848, 1, '2.2.4.2.2.01.00.00.00.00', 'TRIBUTOS ESTADUAIS RENEGOCIADOS', 'Registra os valores devidos pela entidade relativos a tributos que foram renegociados (ICMS, IPVA e Outros).', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7849, 1, '2.1.8.4.1.96.00.00.00.00', 'AGIO NA EMISSAO DE DEBENTURES', 'Registra os valores de ágio na emissão de debentures, que são os valores recebidos que supera o de resgate desses títulos na data do próprio recebimento ou do valor formalmente atribuído as debentures.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7850, 1, '2.1.8.4.1.99.00.00.00.00', 'OUTROS TITULOS', 'Registra outros títulos de dívida com vencimento até o termino do exercício seguinte.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7851, 1, '2.1.8.5.1.01.00.00.00.00', 'DIVIDENDOS A PAGAR - TESOURO ESTADUAL/MUNICIPAL', 'Registra os valores a serem pagos ao tesouro estadual/municipal a título de dividendos.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7852, 1, '2.1.8.5.1.02.00.00.00.00', 'DIVIDENDOS A PAGAR - TERCEIROS', 'Registra os valores a serem pagos a terceiros a título de dividendos.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7853, 1, '2.1.8.5.1.03.00.00.00.00', 'JUROS SOBRE CAPITAL PRÓPRIO A PAGAR', 'Registra os valores a serem pagos a título de remuneração do capital próprio.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7854, 1, '2.1.8.6.1.01.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE ATIVOS CONSTRUÍDOS PELA SPE', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à incorporação dos ativos.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7855, 1, '2.1.8.6.1.02.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DA CONTRAPRESTAÇÃO DE SERVIÇOS EM CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7856, 1, '2.1.8.8.1.01.01.00.00.00', 'RPPS - RETENÇÕES SOBRE VENCIMENTOS E VANTAGENS', 'Registra o valor das retenções procedidas em pagamentos de servidores a ser recolhido ao RPPS do servidor.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7857, 1, '2.1.8.8.1.01.02.00.00.00', 'INSS', 'Registra o valor das retenções procedidas em pagamentos de empregados regidos pela consolidação das leis do trabalho (CLT) ou de serviços de terceiros, a ser recolhido ao instituto nacional do seguro social/previdência social.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7858, 1, '2.1.8.8.1.01.03.00.00.00', ' ENCARGOS SOCIAIS - OUTRAS ENTIDADES', 'Registra o valor a ser recolhido a outras entidades, em função de dispositivos legais com as quais a entidade mantenha convenio de arrecadação direta. Exemplo: FNDE, INCRA, DPC, SENAI, SESI, SENAC, FAER, SEBRAE, SENAT, SESCOOP, SESC.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7859, 1, '2.1.8.8.1.01.04.00.00.00', 'IMPOSTO SOBRE A RENDA RETIDO NA FONTE - IRRF', 'Registra os valores referentes as retenções na fonte, pela entidade, do imposto sobre a renda, relativas as importâncias pagas a terceiros ou creditadas a servidores ou empregados, sobre as quais incida o referido imposto.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7860, 1, '2.1.8.8.1.01.05.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Registra os valores relativos as indenizações e restituições de tributos com autorizações concedidas, porém não devolvidas aos legítimos credores pela entidade.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7861, 1, '2.1.8.8.1.01.06.00.00.00', 'IMPOSTOS E CONTRIBUIÇÕES DIVERSOS', 'Registra os valores referentes as retenções na fonte, pela entidade, de impostos e contribuições diversos, relativas as importâncias pagas a terceiros, sobre os quais incidam os referidos tributos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7862, 1, '2.1.8.8.1.01.07.00.00.00', 'ICMS ', 'Registra os valores relativos ao imposto sobre a circulação de mercadorias e serviços retidos por foca de legislação ou por acordo entre as partes envolvidas na transação em consignações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7863, 1, '2.1.8.9.1.11.00.00.00.00', 'CONTRATOS DE GESTAO A PAGAR', 'Registra as obrigações relativas aos contratos entre a administração pública e a iniciativa privada para gestão de atividades típicas de governo', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7864, 1, '2.1.8.8.1.01.08.00.00.00', 'ISS ', 'Registra os valores do imposto sobre serviços retidos em consignações por forca de legislação, circunstancias ou acordo entre as partes envolvidos na transação, em situações que a entidade como parte contratante, atue como substituta tributária.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7865, 1, '2.1.8.8.1.01.09.00.00.00', 'OUTROS TRIBUTOS ESTADUAIS E MUNICIPAIS', 'Registra os valores em consignação relativos a outros tributos a serem repassados aos estados e municípios.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7866, 1, '2.1.8.8.1.01.10.00.00.00', 'PENSAO ALIMENTICIA', 'Registra os valores das retenções em folha procedidas em pagamentos a empregados ou a servidores, autorizados por esses ou por determinação judicial, a título de pensão alimentícia.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7867, 1, '2.1.8.8.1.01.11.00.00.00', 'PLANOS DE PREVIDENCIA E ASSISTENCIA MEDICA', 'Registra os descontos efetuados em folhas de pagamento do pessoal para posterior recolhimento a favor de instituições de previdência e assistência medica.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7868, 1, '2.1.8.8.1.01.12.00.00.00', 'ASSISTENCIA A SAUDE - ADMINISTRAÇÃO PROPRIA', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, que serão administrados pelo próprio órgão/entidade para assistência a saúde de seus servidores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7869, 1, '2.1.8.8.1.01.13.00.00.00', 'RETENÇÕES - ENTIDADES REPRESENTATIVAS DE CLASSES', 'Registra os descontos efetuados em folhas de pagamento de pessoal para posterior recolhimento a favor de instituições representativas de classe.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7870, 1, '2.1.8.8.1.01.14.00.00.00', 'RETENÇÕES - PLANOS DE SEGUROS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes as contribuições a planos de seguro de vida em grupo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7871, 1, '2.1.8.8.1.01.15.00.00.00', 'RETENÇÕES - EMPRÉSTIMOS E FINANCIAMENTOS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes a empréstimos e financiamentos concedidos por terceiros, bem como retenções de parcelas de consórcios e segurados do INSS.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7872, 1, '2.1.8.8.1.01.16.00.00.00', 'RETENÇÃO RELATIVA A VALE TRANSPORTE', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de vale-transporte. Trata-se dos valores referentes aos 6% instituídos pelo artigo 9º do decreto nº 95.247/1987 e suas alterações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7873, 1, '2.1.8.8.1.01.17.00.00.00', 'RETENÇÃO RELATIVA A VALE ALIMENTAÇÃO', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de cartão-alimentação/refeição.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7874, 1, '2.1.8.8.1.01.99.00.00.00', 'OUTROS CONSIGNATARIOS', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, em confiança, por diversos contribuintes sujeitos a recolhimentos e ou pagamentos aos favorecidos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7875, 1, '2.1.8.8.1.02.00.00.00.00', 'GARANTIAS', 'Compreende a garantia do cumprimento de uma obrigação, que se efetiva com o oferecimento de bens ou recursos financeiros ou ainda retenções de um percentual do valor do contrato como garantia de cumprimento do mesmo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7876, 1, '2.1.8.8.1.03.01.00.00.00', 'DEPOSITOS RECEBIDOS POR DETERMINAÇÃO JUDICIAL', 'Registra os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7877, 1, '2.1.8.8.1.03.02.00.00.00', 'DEPOSITOS PARA RECURSOS JUDICIAIS', 'Registra os valores relativos as obrigações contraídas por depósitos efetuados a favor da entidade destinados ao exercício de direito de recurso junto a justiça e encontrados vinculados em contas bancarias.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7878, 1, '2.2.4.3.1.01.00.00.00.00', 'TRIBUTOS MUNICIPAIS RENEGOCIADOS', 'Registra os valores devidos pela entidade relativos a tributos que foram renegociados (ISS, IPTU e Outros).', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7879, 1, '2.1.8.8.1.04.01.00.00.00', 'DEPOSITOS E CAUÇÕES', 'Registra os valores das obrigações exigíveis contraídas com o recebimento de depósitos e/ou cauções vinculados a contratos ou outros instrumentos, para garantias de operações especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7880, 1, '2.1.8.8.1.04.02.00.00.00', 'DEPOSITOS ESPECIAIS', 'Registra o valor relativo a retenções de depósitos vinculados a restituir no curso do exercício seguinte a entidade transferidora, decorrente de contratos ou instrumentos congêneres, convenção, acordo ou ajuste.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7881, 1, '2.1.8.8.1.04.03.00.00.00', 'DEPOSITOS DE TERCEIROS', 'Registra os valores relativos aos depósitos de terceiros de diversas origens, inclusive desconhecidas, não reclamados pelos credores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7882, 1, '2.1.8.8.1.04.04.00.00.00', 'DEPOSITOS DE VEICULOS ALIENAVEIS', 'Registra o valor dos recebimentos provenientes da alienação de veículos removidos, recolhidos ou apreendidos de acordo com a resolução do Contran nº 178/2005, pendentes de transferências diversas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7883, 1, '2.1.8.8.1.04.05.00.00.00', 'DEPOSITOS A TRANSFERIR', 'Registra os valores das retenções de depósitos de diversas origens recebidos por UG central cuja devolução deve ser realizada no curso do exercício seguinte.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7884, 1, '2.1.8.8.1.04.99.00.00.00', 'OUTROS DEPOSITOS', 'Registra compromissos exigíveis até o termino do exercício seguinte, decorrentes de recebimentos a título de depósitos, para os quais não haja contas especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7885, 1, '2.1.8.8.1.99.00.00.00.00', 'OUTROS VALORES RESTITUIVEIS', 'Compreende outros valores restituíveis em posse da entidade, no papel de fiel depositaria.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (7886, 1, '2.1.8.9.1.01.01.00.00.00', 'INDENIZAÇÕES A SERVIDORES', 'Registra o valor das despesas a serem indenizadas decorrentes de liquidação de pagamentos efetuados com auxílio - alimentação bem como reembolsos de gastos realizados pelos servidores custeados pelo programa de assistência a saúde e benefícios sociais do órgão responsável.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7887, 1, '2.1.8.9.1.01.02.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES DIVERSAS DO EXERCÍCIO', 'Registra os valores relativos as diversas indenizações e restituições no exercício.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7888, 1, '2.1.8.9.1.01.03.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES DIVERSAS DE EXERCÍCIOS ANTERIORES', 'Registra os valores relativos as diversas indenizações e restituições de exercícios anteriores.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7889, 1, '2.1.8.9.1.01.04.00.00.00', 'RESTITUIÇÕES DE CONTRIBUIÇÕES PREVIDENCIÁRIAS', 'Registra os valores relativos a restituições de contribuições previdenciárias a pagar.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7890, 1, '2.1.8.9.1.03.00.00.00.00', 'SUPRIMENTOS DE FUNDOS A PAGAR', 'Registra os valores relativos aos suprimentos de fundos a pagar.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7891, 1, '2.1.8.9.1.09.02.00.00.00', 'TERMOS DE PARCERIA A PAGAR DE EXERCÍCIOS ANTERIORES', 'Registra os valores a pagar referentes a termos de parceria firmados em exercícios anteriores.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7892, 1, '2.1.8.9.1.10.00.00.00.00', 'TERMO DE COMPROMISSO A PAGAR', 'Compreende os valores a pagar retidos a título de termo de compromisso.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7893, 1, '2.1.8.9.1.13.00.00.00.00', 'TRANSFERÊNCIAS ORCAMENTARIAS A LIBERAR ', 'Registra os compromissos exigíveis provenientes da apropriação da despesa de transferência.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7894, 1, '2.1.8.9.1.14.00.00.00.00', 'CONSÓRCIOS A PAGAR', 'Registra as obrigações a pagar junto a consórcios.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7895, 1, '2.2.1.1.1.01.00.00.00.00', 'PESSOAL A PAGAR ', 'Registra os valores relativos a vencimentos e vantagens fixas e variáveis devidas aos servidores civis e militares, bem como as aposentadorias, reformas e pensões.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7937, 1, '2.2.2.1.2.02.01.00.00.00', 'EMPRÉSTIMOS DO RPPS A PAGAR ', 'Registra o saldo a pagar referente a empréstimos obtidos com recursos do RPPS, anteriormente à atual proibição legal.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (7896, 1, '2.2.1.1.1.03.01.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ESPECIAL - ANTES DE 05/05/2000 ', 'Registra as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões,  sujeitos ao regime especial instituído pela emenda constitucional 62/2009, anteriores a 05/05/2000 (Lei de Responsabilidade Fiscal).', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7897, 1, '2.2.1.1.1.03.02.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ESPECIAL - A PARTIR DE 05/05/2000 - VENCIDOS E NÃO PAGOS', 'Registra as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito,  sujeitos ao regime especial instituído pela emenda constitucional 62/2009, a partir de 05/05/2000 (Lei de Responsabilidade Fiscal), não pagos durante a execução do orçamento em que deveriam ter sido incluídos, caso fosse observada à regra geral, prevista no §5º do artigo 100 da CF/88.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7898, 1, '2.2.1.1.1.03.03.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ESPECIAL - A PARTIR DE 05/05/2000 - NÃO VENCIDOS', 'Registra as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito,  sujeitos ao regime especial instituído pela emenda constitucional 62/2009, a partir de 05/05/2000 (Lei de Responsabilidade Fiscal), dentro do prazo de pagamento caso fosse observada a regra geral, estabelicida no § 5º do artigo 100 da CF/88.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7899, 1, '2.2.1.1.1.04.01.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ORDINÁRIO - ANTES DE 05/05/2000 ', 'Registra as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito,  não sujeitos ao regime especial instituído pela emenda constitucional 62/2009, anteriores a 05/05/2000 (Lei de Responsabilidade Fiscal).', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (7900, 1, '2.2.1.1.1.04.02.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ORDINÁRIO - A PARTIR DE 05/05/2000 - VENCIDOS E NÃO PAGOS', 'Registra as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito,  não sujeitos ao regime especial instituído pela emenda constitucional 62/2009, a partir de 05/05/2000 (Lei de Responsabilidade Fiscal), não pagos durante a execução do orçamento em que deveriam ter sido incluídos em observância ao disposto no § 5º do artigo 100 da CF/88.', 2, true, 2, 'X');
