insert into contabilidade.planocontadetalhe values (8022, 1, '2.2.7.2.1.03.01.00.00.00', 'APOSENTADORIAS/PENSÕES/OUTROS BENEFÍCIOS CONCEDIDOS DO PLANO PREVIDENCIÁRIO DO RPPS ', 'Registra, de acordo com o resultado da avaliação atuaria, o valor presente das prestações futuras dos benefícios já concedidos aos assistidos e beneficiários em gozo de benefício de prestação continuada.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8023, 1, '2.2.7.2.1.04.01.00.00.00', 'APOSENTADORIAS/PENSÕES/OUTROS BENEFÍCIOS A CONCEDER DO PLANO PREVIDENCIÁRIO DO RPPS ', 'Registra, de acordo com o resultado da avaliação atuarial, o valor presente dos benefícios a serem concedidos aos integrantes da geração atual que ainda não estejam em gozo de benefícios de prestação continuada.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8024, 1, '2.2.7.2.1.06.01.00.00.00', 'PROVISÃO ATUARIAL PARA OSCILAÇÃO DE RISCOS       ', 'Registra, de acordo com a nota técnica atuarial, a provisão atuarial correspondente a eventuais desvios dos compromissos esperados ou pela adoção de bases técnicas que não se adaptam ao plano.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8025, 1, '2.2.7.2.1.07.01.00.00.00', 'AJUSTE DE RESULTADO ATUARIAL SUPERAVITÁRIO   ', 'Registra, de acordo com o resultado da avaliação atuarial, provisão atuarial para ajuste do plano previdenciário quando o plano apresentar resultado superavitário, considerando o artigo 25 da Portaria MPS nº 403/2008.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8026, 1, '2.2.7.2.1.07.02.00.00.00', 'PROVISÃO ATUARIAL PARA OSCILAÇÃO DE RISCOS', 'Registra, de acordo com o resultado da avaliação atuarial, provisão atuarial para ajuste do plano previdenciário quando o plano apresentar oscilação de riscos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8027, 1, '2.2.7.2.1.07.03.00.00.00', 'PROVISÃO ATUARIAL PARA BENEFÍCIOS A REGULARIZAR', 'Registra, de acordo com o resultado da avaliação atuarial, provisão atuarial para ajuste do plano previdenciário quando o plano apresentar benefícios a regularizar.', 2, true, 2, 'P');
