insert into contabilidade.planocontadetalhe values (8030, 1, '2.2.7.3.1.01.01.00.00.00', 'PROVISÃO PARA PAGAMENTO DE AUTOS DE INFRAÇÃO NÃO RECORRIDOS', 'Registra os valores das provisões para pagamento de autos de infração já lavrados, para os quais a unidade ainda não apresentou recurso administrativo.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8031, 1, '2.2.7.3.1.01.02.00.00.00', 'PROVISÃO PARA PAGAMENTO DE AUTOS DE INFRAÇÃO RECORRIDOS', 'Registra os valores das provisões para pagamento de autos de infração já lavrados, para os quais a unidade apresentou recurso administrativo.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8032, 1, '2.2.7.3.1.02.01.00.00.00', 'PROVISÃO PARA PAGAMENTOS DE AUTUAÇÕES FISCAIS NA ESFERA JUDICIAL NÃO RECORRIDAS', 'Registra os valores das provisões para pagamento de autuações fiscais que se encontram na esfera judicial e que não estão em fase recursal.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8033, 1, '2.2.7.3.1.02.02.00.00.00', 'PROVISÃO PARA PAGAMENTOS DE AUTUAÇÕES FISCAIS NA ESFERA JUDICIAL RECORRIDAS', 'Registra os valores das provisões para pagamento de autuações fiscais que se encontram na esfera judicial em fase recursal.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8034, 1, '2.2.7.4.1.01.00.00.00.00', 'PROVISÃO PARA INDENIZAÇÕES CÍVEIS', 'Registra os valores das provisões constituídas para o pagamento de indenizações cíveis.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8035, 1, '2.2.7.4.1.02.00.00.00.00', 'PROVISÃO PARA PAGAMENTO DE SEGUROS - SINISTROS A LIQUIDAR', 'Registra os valores das provisões para pagamento de seguros referentes a sinistros a liquidar.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8036, 1, '2.2.7.4.1.03.00.00.00.00', 'PROVISÃO PARA PAGAMENTO DE SEGUROS - SINISTROS CIENTIFICADOS', 'Registra os valores das provisões para pagamento de seguros referentes a sinistros já cientificados.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8037, 1, '2.2.7.4.1.04.00.00.00.00', 'PROVISÃO PARA PAGAMENTO DE SEGUROS - SINISTROS NÃO CIENTIFICADOS', 'Registra os valores das provisões para pagamento de seguros, referentes a sinistros não cientificados.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8038, 1, '2.2.7.9.1.01.00.00.00.00', 'PROVISÃO PARA GARANTIAS', 'Registra os valores das provisões destinadas a cobrir despesas com a prestação de garantia relativas a produtos vendidos.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8039, 1, '2.2.7.9.1.02.00.00.00.00', 'PROVISÃO PARA REESTRUTURAÇÃO', 'Registra os valores das provisões destinadas a cobrir obrigações oriundas da venda ou extinção de uma linha de negócios, fechamento de fábricas ou locais de negócios, mudanças na estrutura da administração e reorganizações com efeito relevante na natureza e foco das operações da entidade.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8040, 1, '2.2.7.9.1.03.01.00.00.00', 'PROVISÕES DO FCVS - RISCOS EXPIRADOS', 'Registra os resíduos de contratos liquidados pelo mutuário com cobertura do FCVS, de acordo com a avaliação atuarial.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8041, 1, '2.2.7.9.1.03.02.00.00.00', 'PROVISÕES DO FCVS - RISCOS NÃO EXPIRADOS', 'Registra o montante de contratos com cobertura do FCVS em fase de pagamento pelos mutuários, de acordo com a avaliação atuarial.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8042, 1, '2.2.8.6.1.01.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à incorporação dos ativos.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (8043, 1, '2.2.8.6.2.02.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DA CONTRAPRESTAÇÃO DE SERVIÇOS EM CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger a contraprestação referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, true, 2, 'X');
insert into contabilidade.planocontadetalhe values (8071, 1, '2.2.8.8.1.04.05.00.00.00', 'DEPOSITOS A TRANSFERIR', 'Registra os valores das retenções de depósitos de diversas origens recebidos por UG central cuja devolução deve ser realizada no curso do exercício seguinte.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8044, 1, '2.2.8.8.1.04.04.00.00.00', 'DEPOSITOS DE VEICULOS ALIENAVEIS', 'Registra o valor dos recebimentos provenientes da alienação de veículos removidos, recolhidos ou apreendidos de acordo com a resolução do Contran nº 178/2005, pendentes de transferências diversas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8045, 1, '2.2.8.8.1.01.01.00.00.00', 'RPPS - RETENÇÕES SOBRE VENCIMENTOS E VANTAGENS', 'Registra o valor das retenções procedidas em pagamentos de servidores a ser recolhido ao RPPS do servidor.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8046, 1, '2.2.8.8.1.01.02.00.00.00', 'INSS', 'Registra o valor das retenções procedidas em pagamentos de empregados regidos pela consolidação das leis do trabalho (CLT) ou de serviços de terceiros, a ser recolhido ao instituto nacional do seguro social/previdência social.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8047, 1, '2.2.8.8.1.01.03.00.00.00', 'ENCARGOS SOCIAIS - OUTRAS ENTIDADES', 'Registra o valor a ser recolhido a outras entidades, em função de dispositivos legais com as quais a entidade mantenha convenio de arrecadação direta. Exemplo: FNDE, INCRA, DPC, SENAI, SESI, SENAC, FAER, SEBRAE, SENAT, SESCOOP, SESC.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8048, 1, '2.3.3.9.1.03.00.00.00.00', 'RESERVA DE CORREÇÃO MONETÁRIA ESPECIAL DEC. LEI 1.598/77', 'Registra os valores do resultado da correção monetária especial, conforme o Decreto-lei nº 1.598/77.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8049, 1, '2.2.8.8.1.01.04.00.00.00', 'IMPOSTO SOBRE A RENDA RETIDO NA FONTE - IRRF', 'Registra os valores referentes as retenções na fonte, pela entidade, do imposto sobre a renda, relativas as importâncias pagas a terceiros ou creditadas a servidores ou empregados, sobre as quais incida o referido imposto.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8050, 1, '2.2.8.8.1.01.05.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Registra os valores relativos as indenizações e restituições de tributos com autorizações concedidas, porém não devolvidas aos legítimos credores pela entidade.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8051, 1, '2.2.8.8.1.01.06.00.00.00', 'IMPOSTOS E CONTRIBUIÇÕES DIVERSOS', 'Registra os valores referentes as retenções na fonte, pela entidade, de impostos e contribuições diversos, relativas as importâncias pagas a terceiros, sobre os quais incidam os referidos tributos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8052, 1, '2.2.8.8.1.01.07.00.00.00', 'ICMS ', 'Registra os valores relativos ao imposto sobre a circulação de mercadorias e serviços retidos por foca de legislação ou por acordo entre as partes envolvidas na transação em consignações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8053, 1, '2.2.8.8.1.01.08.00.00.00', 'ISS ', 'Registra os valores do imposto sobre serviços retidos em consignações por forca de legislação, circunstancias ou acordo entre as partes envolvidos na transação, em situações que a entidade como parte contratante, atue como substituta tributária.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8054, 1, '2.2.8.8.1.01.09.00.00.00', 'OUTROS TRIBUTOS ESTADUAIS E MUNICIPAIS', 'Registra os valores em consignação relativos a outros tributos a serem repassados aos estados e municípios.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8055, 1, '2.2.8.8.1.01.10.00.00.00', 'PENSAO ALIMENTICIA', 'Registra os valores das retenções em folha procedidas em pagamentos a empregados ou a servidores, autorizados por esses ou por determinação judicial, a título de pensão alimentícia.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8056, 1, '2.2.8.8.1.01.11.00.00.00', 'PLANOS DE PREVIDENCIA E ASSISTENCIA MEDICA', 'Registra os descontos efetuados em folhas de pagamento do pessoal para posterior recolhimento a favor de instituições de previdência e assistência medica.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8057, 1, '2.2.8.8.1.01.12.00.00.00', 'ASSISTENCIA A SAUDE - ADMINISTRAÇÃO PROPRIA', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, que serão administrados pelo próprio órgão/entidade para assistência a saúde de seus servidores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8058, 1, '2.2.8.8.1.01.13.00.00.00', 'RETENÇÕES - ENTIDADES REPRESENTATIVAS DE CLASSES', 'Registra os descontos efetuados em folhas de pagamento de pessoal para posterior recolhimento a favor de instituições representativas de classe.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8059, 1, '2.2.8.8.1.01.14.00.00.00', 'RETENÇÕES - PLANOS DE SEGUROS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes as contribuições a planos de seguro de vida em grupo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8060, 1, '2.2.8.8.1.01.15.00.00.00', 'RETENÇÕES - EMPRÉSTIMOS E FINANCIAMENTOS', 'Registra os valores das retenções efetuadas em folha de pagamento dos servidores referentes a empréstimos e financiamentos concedidos por terceiros, bem como retenções de parcelas de consórcios e segurados do INSS.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8061, 1, '2.2.8.8.1.01.16.00.00.00', 'RETENÇÃO RELATIVA A VALE TRANSPORTE', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de vale-transporte. Trata-se dos valores referentes aos 6% instituídos pelo artigo 9º do decreto nº 95.247/1987 e suas alterações.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8062, 1, '2.2.8.8.1.01.17.00.00.00', 'RETENÇÃO RELATIVA A VALE ALIMENTAÇÃO', 'Registra os valores das consignações retidas pela entidade a serem repassados para as empresas prestadoras dos serviços de fornecimento de cartão-alimentação/refeição.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8063, 1, '2.2.8.8.1.01.99.00.00.00', 'OUTROS CONSIGNATARIOS', 'Registra os valores das consignações retidos pela entidade, ou entregues a essa, em confiança, por diversos contribuintes sujeitos a recolhimentos e ou pagamentos aos favorecidos.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8064, 1, '2.2.8.8.1.02.00.00.00.00', 'GARANTIAS', 'Compreende a garantia do cumprimento de uma obrigação, que se efetiva com o oferecimento de bens ou recursos financeiros ou ainda retenções de um percentual do valor do contrato como garantia de cumprimento do mesmo.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8065, 1, '2.2.8.8.1.03.01.00.00.00', 'DEPOSITOS RECEBIDOS POR DETERMINAÇÃO JUDICIAL', 'Registra os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8066, 1, '2.2.8.8.1.03.02.00.00.00', 'DEPOSITOS PARA RECURSOS JUDICIAIS', 'Registra os valores relativos as obrigações contraídas por depósitos efetuados a favor da entidade destinados ao exercício de direito de recurso junto a justiça e encontrados vinculados em contas bancarias.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8067, 1, '2.2.8.8.1.04.01.00.00.00', 'DEPOSITOS E CAUÇÕES', 'Registra os valores das obrigações exigíveis contraídas com o recebimento de depósitos e/ou cauções vinculados a contratos ou outros instrumentos, para garantias de operações especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8068, 1, '2.2.8.8.1.04.02.00.00.00', 'DEPOSITOS ESPECIAIS', 'Registra o valor relativo a retenções de depósitos vinculados a restituir no curso do exercício seguinte a entidade transferidora, decorrente de contratos ou instrumentos congêneres, convenção, acordo ou ajuste.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8069, 1, '2.2.8.8.1.04.03.00.00.00', 'DEPOSITOS DE TERCEIROS', 'Registra os valores relativos aos depósitos de terceiros de diversas origens, inclusive desconhecidas, não reclamados pelos credores.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8072, 1, '2.2.8.8.1.04.99.00.00.00', 'OUTROS DEPOSITOS', 'Registra compromissos exigíveis até o termino do exercício seguinte, decorrentes de recebimentos a título de depósitos, para os quais não haja contas especificas.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8073, 1, '2.2.8.8.1.99.00.00.00.00', 'OUTROS VALORES RESTITUIVEIS', 'Compreende outros valores restituíveis em posse da entidade, no papel de fiel depositaria.', 2, true, 2, 'F');
insert into contabilidade.planocontadetalhe values (8074, 1, '2.3.3.9.1.01.00.00.00.00', 'PRÊMIO NA EMISSÃO DE DEBÊNTURES', 'Registra os valores de prêmio na emissão de debêntures, que ocorrem quando o preço da emissão de debêntures for superior a seu valor nominal. Esta conta não consta mais dentre as rubricas de reservas de capital e assim deve ser utilizada apenas durante o período necessário para utilização dos saldos remanescentes nela.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8075, 1, '2.3.3.9.1.02.00.00.00.00', 'RESERVAS DE DOAÇÕES E SUBVENÇÕES PARA INVESTIMENTOS', 'Registra os valores de doações e subvenções para investimentos. Esta conta não consta mais dentre as rubricas de reservas de capital e assim deve ser utilizada apenas durante o período necessário para utilização dos saldos remanescentes nela.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8076, 1, '2.3.3.9.1.99.00.00.00.00', 'DEMAIS RESERVAS DE CAPITAL', 'Registra os valores das demais reservas de capital, não contabilizáveis em outras contas deste plano.', 2, true, 2, 'P');
