insert into contabilidade.planocontadetalhe values (8079, 1, '2.3.3.9.2.03.00.00.00.00', 'RESERVA DE CORREÇÃO MONETÁRIA ESPECIAL DEC. LEI 1.598/77', 'Registra os valores do resultado da correção monetária especial, conforme o Decreto-lei nº 1.598/77.', 2, true, 2, 'P');
insert into contabilidade.planocontadetalhe values (8080, 1, '2.3.3.9.2.99.00.00.00.00', 'DEMAIS RESERVAS DE CAPITAL', 'Registra os valores das demais reservas de capital, não contabilizáveis em outras contas deste plano.', 2, true, 2, 'P');
