insert into contabilidade.planocontadetalhe values (8085, 1, '4.2.1.1.5.99.00.00.00.00', 'OUTRAS CONTRIBUIÇÕES SOCIAIS - RPPS', 'Registra o valor das vpa oriundas de contribuições previdenciarias nao en-quadradas nos subitens anteriores.', 2, true, 2, 'N');
