insert into contabilidade.planocontadetalhe values (8221, 1, '4.4.2.4.1.16.00.00.00.00', 'MULTAS E JUROS DE DÍVIDA ATIVA TRIBUTÁRIA', 'Registra os valores de multas e juros de dívida ativa tributária.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (8222, 1, '4.3.3.1.1.53.00.00.00.00', 'SERVICO DE COMPENSAÇÃO DE VARIAÇÕES SALARIAIS', 'Registra as variações patrimoniais aumentativas provenientes da cobrança de contribuições sobre o valor das prestações pagas pelos mutuários do sistema financeiro de habitação-SFH e sobre o saldo dos financiamentos imobiliários concedidos pelos agentes financeiros do SFH, com cobertura do FCVS.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (8223, 1, '4.3.3.1.1.54.00.00.00.00', 'GARANTIAS E AVAIS', 'Registra as variações patrimoniais aumentativas provenientes da prestação de garantias e avais nas operações de credito. Fato gerador: concessão de garantias e avais, de natureza não-financeiras.', 2, true, 2, 'N');
insert into contabilidade.planocontadetalhe values (8224, 1, '4.3.3.1.1.55.00.00.00.00', 'CREDENCIAMENTO EMP.PREST.SERVICOS DE VISTORIA', 'Registra as variações patrimoniais aumentativas provenientes do valor a ser pago por empresas na obtenção de credenciamento para prestação de serviços de vistoria. - Destinação legal: órgão arrecadador; - amparo legal: resolução CONTRAN 282, de 26 de junho de 2008.', 2, true, 2, 'N');
