insert into contabilidade.planocontadetalhe values (8226, 1, '4.3.3.1.1.57.00.00.00.00', 'CERTIFICAÇÃO E HOMOLOGAÇÃO ATIVIDADE MINERAL', 'Registra as variações patrimoniais aumentativas provenientes de taxa de serviços cadastrais quando do requerimento de registro de licença e do registro de permissão de lavra garimpeira.', 2, true, 2, 'N');
