insert into contabilidade.planocontadetalhe values (8228, 1, '4.3.3.1.1.59.00.00.00.00', 'SERVICOS FINANCEIROS', 'Registra as variações patrimoniais aumentativas com serviços financeiros', 2, true, 2, 'N');
