insert into contabilidade.planocontadetalhe values (8233, 1, '4.5.1.1.2.04.00.00.00.00', 'RECURSOS ARRECADADOS - RECEBIDOS ', 'Registra o valor recebido de outro órgão ou entidade referente aos recursos que foram arrecadados (classificados como receita) para pagamento de despesas na unidade recebedora.', 2, true, 2, 'N');
