insert into contabilidade.planocontadetalhe values (8242, 1, '4.4.2.4.1.08.00.00.00.00', 'MULTAS E JUROS SOBRE ITR', 'Registra os valores relativos aos créditos a receber, decorrentes do Imposto Sobre a Propriedade Territorial Rural, devidos aos municípios ou ao Distrito Federal devido a convênios firmados com a União, conforme inciso III, parágrafo 4º do artigo 153 da Constituição Federal.', 2, true, 2, 'P');
