insert into contabilidade.planocontadetalhe values (8245, 1, '4.4.2.4.1.11.00.00.00.00', 'MULTAS E JUROS CONTRIBUIÇÕES DE MELHORIA', 'Compreende os valores relativos aos créditos a receber, decorrentes da cobrança dos valores decorrentes da contribuição instituída para fazer face ao custo de obras públicas de que decorra valorização imobiliária.', 2, true, 2, 'P');
