insert into contabilidade.planocontadetalhe values (9272, 1, '6.2.1.0.0.00.00.00.00.00', 'EXECUÇÃO DA RECEITA', 'Compreende o somatório dos valores relativos à realização da receita pública aprovada pela lei orçamentária anual e suas alterações.', 2, false, 0, 'N');
