insert into contabilidade.planocontadetalhe values (8753, 1, '8.3.2.4.1.01.00.00.00.00', 'RECEBIMENTO DE CRÉDITOS INSCRITOS EM DÍVIDA ATIVA TRIBUTÁRIA EM COBRANÇA ADMINISTRATIVA ', 'Registra o recebimento dos valores referentes aos créditos inscritos em dívida ativa tributária, em fase administrativa de cobrança.', 2, true, 1, 'N');
insert into contabilidade.planocontadetalhe values (8754, 1, '8.3.2.4.1.02.00.00.00.00', 'RECEBIMENTO DE CRÉDITOS INSCRITOS EM DÍVIDA ATIVA TRIBUTÁRIA EM COBRANÇA JUDICIAL', 'Registra o recebimento dos valores referentes aos créditos inscritos em dívida ativa tributária, em fase judicial de cobrança.', 2, true, 1, 'N');
