insert into contabilidade.planocontadetalhe values (8758, 1, '8.3.2.5.1.02.00.00.00.00', 'BAIXAS DE CRÉDITOS INSCRITOS EM DÍVIDA ATIVA TRIBUTÁRIA - COBRANÇA JUDICIAL', 'Registra as baixas de créditos inscritos em dívida ativa tributária, em cobrança judicial.', 2, true, 1, 'N');
