insert into contabilidade.planocontadetalhe values (8908, 1, '2.0.0.0.0.00.00.00.00.00', 'PASSIVO E PATRIMÔNIO LIQUIDO', 'Passivo compreende as obrigações existentes da entidade oriundas de eventos passados de cuja liquidação se espera que resulte em fluxo de saída de recursos que incorporem benefícios econômicos ou serviços em potencial. Patrimônio liquido compreende a diferença entre o ativo e o passivo.', 2, false, 0, 'N');
