insert into contabilidade.planocontadetalhe values (9037, 1, '2.1.8.0.0.00.00.00.00.00', 'DEMAIS OBRIGAÇÕES A CURTO PRAZO', 'Compreende as obrigações da entidade junto a terceiros não inclusas nos subgrupos anteriores, com vencimento no curto prazo, inclusive os precatórios decorrentes dessas obrigações, com vencimento no curto prazo.', 2, false, 0, 'N');
