insert into contabilidade.planocontadetalhe values (9039, 1, '2.1.8.1.1.00.00.00.00.00', 'ADIANTAMENTOS DE CLIENTES- CONSOLIDAÇÃO', 'Compreende as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra, nos termos do art. 29, inciso III da LC 101/2000. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9040, 1, '2.1.8.2.0.00.00.00.00.00', 'OBRIGAÇÕES POR DANOS A TERCEIROS', 'Compreende os valores exigíveis no curto prazo, provenientes de danos a terceiros.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9041, 1, '2.1.8.2.1.00.00.00.00.00', 'OBRIGAÇÕES POR DANOS A TERCEIROS - CONSOLIDAÇÃO', 'Compreende os valores exigíveis no curto prazo, provenientes de danos a terceiros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9042, 1, '2.1.8.3.0.00.00.00.00.00', 'ARRENDAMENTO OPERACIONAL A PAGAR', 'Compreende as obrigações a curto prazo referentes a arrendamentos mercantis, nos quais não são transferidos ao arrendador os riscos e benefícios inerentes a propriedade, não havendo a possibilidade de opção de compra do bem arrendado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9043, 1, '2.1.8.3.1.00.00.00.00.00', 'ARRENDAMENTO OPERACIONAL A PAGAR- CONSOLIDAÇÃO', 'Compreende as obrigações a curto prazo referentes a arrendamentos mercantis, nos quais não são transferidos ao arrendador os riscos e benefícios inerentes a propriedade, não havendo a possibilidade de opção de compra do bem arrendado. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9044, 1, '2.1.8.4.0.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A CURTO PRAZO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9045, 1, '2.1.8.4.1.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A CURTO PRAZO- CONSOLIDAÇÃO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9048, 1, '2.1.8.6.0.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto a referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9049, 1, '2.2.1.3.0.00.00.00.00.00', 'BENEFÍCIOS ASSISTENCIAIS A PAGAR', 'Compreende as obrigações a longo prazo relativas aos benefícios assistenciais, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9050, 1, '8.1.2.3.1.07.00.00.00.00', 'TERMO DE PARCERIA', 'Compreende valores relacionados a execução de contratos de obrigações da entidade pública com terceiros referente a termo de parceria', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9051, 1, '2.1.8.6.1.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP- CONSOLIDAÇÃO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto a referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com pessoa ou unidade que não pertença ao OFSS de ente público, alem dos demais fatos que não compreendem transações entre partes.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9052, 1, '2.1.8.7.0.00.00.00.00.00', 'DEPÓSITOS DE INSTITUIÇÕES AUTORIZADAS A OPERAR PELO BACEN', 'Compreende as disponibilidades mantidas no Bacen por diversas instituições, a exemplo das reservas bancárias e depósitos compulsórios. Conta de uso exclusivo da União.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9053, 1, '2.1.8.7.1.00.00.00.00.00', 'DEPÓSITOS DE INSTITUIÇÕES AUTORIZADAS A OPERAR PELO BACEN - CONSOLIDAÇÃO', 'Compreende as disponibilidades mantidas no Bacen por diversas instituições, a exemplo das reservas bancárias e depósitos compulsórios. Conta de uso exclusivo da União. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9054, 1, '2.1.8.8.0.00.00.00.00.00', 'VALORES RESTITUÍVEIS', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9055, 1, '2.1.8.8.1.00.00.00.00.00', 'VALORES RESTITUÍVEIS - CONSOLIDAÇÃO', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9056, 1, '2.1.8.8.1.01.00.00.00.00', 'CONSIGNAÇÕES ', 'Compreende os valores entregues em confiança ou em consignações, geralmente retidos em folha de pagamento de empregados ou servidores ou nos pagamentos referentes a compras de bens e serviços.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9057, 1, '2.1.8.8.1.03.00.00.00.00', 'DEPOSITOS JUDICIAIS', 'Compreende os valores de obrigações, decorrentes de depósitos recebidos por determinação judicial e depósitos para recursos judiciais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9058, 1, '2.1.8.9.0.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9059, 1, '2.1.8.9.1.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO - CONSOLIDAÇÃO', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9060, 1, '2.1.8.9.1.01.00.00.00.00', 'INDENIZAÇÕES E RESTITUIÇÕES', 'Compreende as obrigações relativas a indenizações e restituições.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9061, 1, '2.1.8.9.1.09.00.00.00.00', 'TERMOS DE PARCERIA A PAGAR', 'Compreende os valores a pagar retidos a título de termo de parceria, que é um instrumento jurídico de fomento e gestão das relações de parceria entre organizações do terceiro setor - qualificadas como organização da sociedade civil de interesse público - OSCIP, e o poder público, com objetivo de imprimir maior agilidade gerencial aos projetos, reduzir as excessivas exigências burocráticas e realizar controle dos resultados. O termo de parceria foi instituído e disciplinado pela lei 9.790/99, de 23 de marco de 1999 e decreto nº 3 100/99.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9062, 1, '2.2.2.6.1.04.00.00.00.00', 'ENCARGOS - FINANCIAMENTOS EXTERNOS', 'Compreende os valores exigíveis decorrentes dos encargos de contratos de financiamentos externos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9063, 1, '2.1.8.9.2.00.00.00.00.00', 'OUTRAS OBRIGAÇÕES A CURTO PRAZO-INTRA OFSS', 'Compreende outras obrigações não classificáveis em grupos específicos deste plano de contas, com vencimento no curto prazo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9064, 1, '2.2.0.0.0.00.00.00.00.00', 'PASSIVO NAO-CIRCULANTE', 'Compreende as obrigações conhecidas e estimadas que não atendam a nenhum dos critérios para serem classificadas no passivo circulante.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9065, 1, '2.2.1.0.0.00.00.00.00.00', 'OBRIGAÇÕES TRABALHISTAS, PREVIDENCIÁRIAS E ASSISTENCIAIS A PAGAR A LONGO PRAZO', 'Compreende as obrigações referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, aposentadorias, reformas, pensões e encargos a pagar, benefícios assistenciais, inclusive os precatórios decorrentes dessas obrigações, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9066, 1, '2.2.1.1.0.00.00.00.00.00', 'PESSOAL A PAGAR', 'Compreende as obrigações a longo prazo referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9067, 1, '2.2.1.1.1.00.00.00.00.00', 'PESSOAL A PAGAR- CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo referentes a salários ou remunerações, bem como benefícios aos quais o empregado ou servidor tenha direito, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9068, 1, '2.2.1.1.1.03.00.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ESPECIAL', 'Compreende as obrigações referentes a precatórios de salários ou remunerações, bem como a benefícios aos quais o empregado ou servidor tenha direito, sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9069, 1, '2.2.1.1.1.04.00.00.00.00', 'PRECATÓRIOS DE PESSOAL - REGIME ORDINÁRIO', 'Compreende as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões,  não sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9070, 1, '2.2.1.2.0.00.00.00.00.00', 'BENEFÍCIOS PREVIDENCIÁRIOS A PAGAR', 'Compreende as obrigações a longo prazo referentes a proventos de aposentadoria, reformas ou pensões aos quais o aposentado, reformado ou pensionista tenha direito, quando pagos em data posterior a qual forem incorridos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9071, 1, '2.2.1.2.1.00.00.00.00.00', 'BENEFÍCIOS PREVIDENCIÁRIOS A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo referentes a proventos de aposentadoria, reformas ou pensões aos quais o aposentado, reformado ou pensionista tenha direito, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9072, 1, '2.2.1.2.1.01.00.00.00.00', 'PRECATORIOS DE BENEFICIOS PREVIDENCIÁRIOS', 'Compreende as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9073, 1, '2.2.1.2.1.02.00.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS PREVIDENCIÁRIOS - REGIME ESPECIAL', 'Compreende as obrigações referentes a precatórios de proventos de aposentadoria, reformas ou pensões, sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9074, 1, '2.2.1.3.1.00.00.00.00.00', 'BENEFÍCIOS ASSISTENCIAIS A PAGAR - CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo relativas aos benefícios assistenciais, quando pagos em data posterior a qual forem incorridos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9075, 1, '2.2.1.3.1.02.00.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS ASSISTENCIAIS - REGIME ESPECIAL', 'Compreende as obrigações referentes a precatórios de benefícios assistenciais, sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9076, 1, '2.2.1.3.1.03.00.00.00.00', 'PRECATÓRIOS DE BENEFÍCIOS ASSISTENCIAIS - REGIME ORDINÁRIO', 'Compreende as obrigações referentes a precatórios de benefícios assistenciais,  não sujeitos ao regime especial instituído pela emenda constitucional 62/ 2009.', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9077, 1, '2.2.1.4.0.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9078, 1, '2.2.1.4.1.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR- CONSOLIDAÇÃO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9079, 1, '2.2.1.4.2.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR-INTRA OFSS', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do ente.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9080, 1, '2.2.1.4.3.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR-INTER OFSS - UNIÃO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e a União.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9081, 1, '2.2.1.4.3.01.00.00.00.00', 'INSS A PAGAR', 'Compreende os valores relativos as contribuições a previdência social, incidentes sobre salários e remunerações pagos e sobre serviços de terceiros.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9082, 1, '2.2.1.4.4.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR-INTER OFSS - ESTADO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9083, 1, '2.2.1.4.5.00.00.00.00.00', 'ENCARGOS SOCIAIS A PAGAR-INTER OFSS - MUNICÍPIO', 'Compreende as obrigações a longo prazo das unidades relativas a despesas incorridas e não pagas, em beneficio de seus servidores, empregados e familiares, compulsoriamente ou não, incluindo aquelas que se destinam ao financiamento da seguridade social de responsabilidade do poder público e as demais contribuições sociais. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9084, 1, '2.2.2.0.0.00.00.00.00.00', 'EMPRÉSTIMOS E FINANCIAMENTOS A LONGO PRAZO', 'Compreende as obrigações financeiras da entidade a titulo de empréstimos, bem como as aquisições efetuadas diretamente com o fornecedor, com vencimentos no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9085, 1, '2.2.2.1.0.00.00.00.00.00', 'EMPRÉSTIMOS A LONGO PRAZO - INTERNO', 'Compreende os empréstimos contratuais ou mobiliários assumidos dentro do país e transacionados, em regra, em moeda nacional, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9086, 1, '2.2.2.1.1.00.00.00.00.00', 'EMPRÉSTIMOS A LONGO PRAZO - INTERNO - CONSOLIDAÇÃO', 'Compreende os empréstimos contratuais ou mobiliários assumidos dentro do país e transacionados, em regra, em moeda nacional, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9087, 1, '2.2.2.1.1.01.00.00.00.00', 'EMPRÉSTIMOS INTERNOS - EM TÍTULOS', 'Compreende os valores das operações de créditos internas decorrentes da emissão de titulos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9088, 1, '2.2.2.1.1.02.00.00.00.00', 'EMPRÉSTIMOS INTERNOS - EM CONTRATOS', 'Compreende os valores das operações de crédito internas em contratos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9151, 1, '6.1.2.0.0.00.00.00.00.00', 'EXECUÇÃO DO PLOA', 'Compreende o somatório dos valores monetários de receitas e despesas previstos no projeto de lei orçamentária durante as suas fases de tramitação.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9089, 1, '2.2.2.1.2.00.00.00.00.00', 'EMPRÉSTIMOS A LONGO PRAZO - INTERNO - INTRA OFSS', 'Compreende os empréstimos contratuais ou mobiliários assumidos dentro do país e transacionados, em regra, em moeda nacional, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9090, 1, '2.2.2.1.2.02.00.00.00.00', 'EMPRÉSTIMOS INTERNOS - EM CONTRATOS', 'Compreende os valores das operações de crédito internas em contratos.', 2, false, 0, 'P');
