insert into contabilidade.planocontadetalhe values (9092, 1, '2.2.2.1.3.01.00.00.00.00', 'EMPRÉSTIMOS INTERNOS - EM TÍTULOS', 'Compreende os valores das operações de créditos internas decorrentes da emissão de titulos.', 2, false, 0, 'N');
