insert into contabilidade.planocontadetalhe values (9150, 1, '2.2.4.3.1.00.00.00.00.00', 'OBRIGAÇÕES FISCAIS A LONGO PRAZO COM OS MUNICÍPIOS - CONSOLIDAÇÃO', 'Compreende as obrigações das entidades com o governo municipal relativas a impostos, taxas e contribuições, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
