insert into contabilidade.planocontadetalhe values (9153, 1, '2.2.4.3.5.00.00.00.00.00', 'OBRIGAÇÕES FISCAIS A LONGO PRAZO COM OS MUNICÍPIOS-INTER OFSS - MUNICÍPIO', 'Compreende as obrigações das entidades com o governo municipal relativas a impostos, taxas e contribuições, com vencimento no longo prazo.  Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9154, 1, '2.2.7.0.0.00.00.00.00.00', 'PROVISÕES A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9155, 1, '2.2.7.1.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS TRABALHISTAS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de reclamações trabalhistas, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9156, 1, '2.2.7.1.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS TRABALHISTAS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de reclamações trabalhistas, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9157, 1, '2.2.7.2.0.00.00.00.00.00', 'PROVISÕES MATEMÁTICAS PREVIDÊNCIÁRIAS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a futuros benefícios previdenciários a serem pagos aos contribuintes, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9158, 1, '2.2.7.2.1.00.00.00.00.00', 'PROVISÕES MATEMÁTICAS PREVIDÊNCIÁRIAS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a futuros benefícios previdenciários a serem pagos aos contribuintes, comprobabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9159, 1, '2.2.7.2.1.01.00.00.00.00', 'PLANO FINANCEIRO - PROVISOES DE BENEFICIOS CONCEDIDOS   ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios já concedidos aos assistidos e beneficiários em gozo de beneficio de prestação continuada.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9160, 1, '2.2.7.2.1.02.00.00.00.00', 'PLANO FINANCEIRO - PROVISOES DE BENEFICIOS A CONCEDER            ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios a conceder para a geração atual (servidores ativos).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9161, 1, '2.2.7.2.1.03.00.00.00.00', 'PLANO PREVIDENCIARIO - PROVISOES DE BENEFICIOS CONCEDIDOS            ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios já concedidos aos assistidos e beneficiários em gozo de beneficio de prestação continuada.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9162, 1, '2.2.7.2.1.04.00.00.00.00', 'PLANO PREVIDENCIARIO - PROVISOES DE BENEFICIOS A CONCEDER              ', 'Compreende, de acordo com o resultado da avaliação atuarial, a provisão matemática correspondente ao valor presente liquido das prestações futuras dos benefícios a conceder para a geração atual (servidores ativos).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9163, 1, '2.2.7.2.1.05.00.00.00.00', ' PLANO PREVIDENCIARIO - PLANO DE AMORTIZAÇÃO                            ', 'Compreende, de acordo com resultado da avaliação atuarial, o valor presente liquido dos direitos incorporados ao patrimônio do RPPS com base em legislação especifica, bem como registro tempestivo do plano de amortização implementado em lei do ente federativo, considerando que o ajuste contábil das reservas matemáticas somente ocorrera na apresentação de uma nova reavaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9164, 1, '2.2.7.2.1.06.00.00.00.00', ' PROVISOES ATUARIAIS PARA AJUSTES DO PLANO FINANCEIRO      ', 'Compreende o somatório das provisões atuariais constituídas para ajustes do plano financeiro, conforme o resultado da avaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9165, 1, '2.2.7.2.1.07.00.00.00.00', ' PROVISOES ATUARIAIS PARA AJUSTES DO PLANO PREVIDENCIARIO      ', 'Compreende o somatório das provisões atuariais constituídas para ajustes do plano previdenciário, conforme o resultado da avaliação atuarial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9166, 1, '2.2.7.3.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS FISCAIS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valor incertos, relacionados ao pagamento de autuações fiscais, com probabilidade de ocorrerem no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9167, 1, '2.2.7.3.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS FISCAIS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados ao pagamento de autuações fiscais, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9168, 1, '2.2.7.3.1.01.00.00.00.00', 'PROVISÃO PARA PAGAMENTOS REFERENTES A AUTOS DE INFRAÇÃO', 'Compreende os valores das provisões para pagamento de autos de infração já lavrados e que se encontram na esfera administrativa.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9169, 1, '2.2.7.3.1.02.00.00.00.00', 'PROVISÃO PARA PAGAMENTOS DE AUTUAÇÕES FISCAIS - ESFERA JUDICIAL', 'Compreende os valores das provisões para pagamento de autuações fiscais que já se encontram na esfera judicial.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9170, 1, '2.2.7.4.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS CÍVEIS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valor incertos, relacionados a pagamento de indenizações a fornecedores e clientes, com probabilidade de ocorrerem no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9171, 1, '2.2.7.5.0.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9330, 1, '4.5.1.2.2.01.00.00.00.00', 'TRANSFERÊNCIAS FINANCEIRAS RECEBIDAS - INDEPENDENTES DE EXECUÇÃO ORÇAMENTÁRIA', 'Compreende o valor das transferências financeiras recebidas para restos a pagar e outras finalidades independentes da execução orçamentária.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9172, 1, '2.2.7.5.3.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - UNIÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos a outras entidades, conforme previsão legal. Na arrecadação, esta provisão será revertida em conta específica de passivo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9173, 1, '2.2.7.5.4.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - ESTADO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9174, 1, '2.2.7.5.5.00.00.00.00.00', 'PROVISÃO PARA REPARTIÇÃO DE CRÉDITOS A LONGO PRAZO - INTER OFSS - MUNICÍPIO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos créditos tributários e não tributários reconhecidos no lançamento por parte do agente arrecadador, a serem repartidos com outros entes da federação, com probabilidade de ocorrerem no longo prazo. Na arrecadação, esta provisão será revertida em conta especifica de passivo. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um município.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9175, 1, '2.2.7.6.0.00.00.00.00.00', 'PROVISÃO PARA RISCOS DECORRENTES DE CONTRATOS DE PPP A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei n.11.079/2004.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9176, 1, '2.2.7.6.1.00.00.00.00.00', 'PROVISÃO PARA RISCOS DECORRENTES DE CONTRATOS DE PPP A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados aos riscos de demanda, construção, disponibilidade ou outros riscos decorrentes de contratos de PPP, de acordo com a Lei n.11.079/2004. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9177, 1, '2.2.7.7.0.00.00.00.00.00', 'PROVISÃO PARA OBRIGAÇÕES DECORRENTES DA ATUAÇÃO GOVERNAMENTAL A LONGO PRAZO', 'Compreende os passivos de prazo ou de valores incertos relacionados a programas governamentais, como subsídios, auxílios, desonerações, assunção de obrigações ou outros.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9178, 1, '2.2.7.7.1.00.00.00.00.00', 'PROVISÃO PARA OBRIGAÇÕES DECORRENTES DA ATUAÇÃO GOVERNAMENTAL A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os passivos de prazo ou de valores incertos relacionados a programas governamentais, como subsídios, auxílios, desonerações, assunção de obrigações ou outros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9179, 1, '2.2.7.9.0.00.00.00.00.00', 'OUTRAS PROVISÕES A LONGO PRAZO', 'Compreende os demais passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo, não classificadas anteriormente neste plano de contas.', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9180, 1, '2.2.7.9.1.00.00.00.00.00', 'OUTRAS PROVISÕES A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os demais passivos de prazo ou de valor incertos, com probabilidade de ocorrerem no longo prazo, não classificadas anteriormente neste plano de contas. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9181, 1, '2.2.7.9.1.03.00.00.00.00', 'PROVISÕES DO FCVS', 'Compreende, de acordo com a nota técnica atuarial, os valores atuariais dos passivos do fundo de compensação e variações salariais.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9182, 1, '2.2.8.0.0.00.00.00.00.00', 'DEMAIS OBRIGAÇÕES A LONGO PRAZO', 'Compreende as obrigações da entidade junto a terceiros não inclusas nos subgrupos anteriores, inclusive os precatórios decorrentes dessas obrigações, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9183, 1, '2.2.8.1.0.00.00.00.00.00', 'ADIANTAMENTOS DE CLIENTES A LONGO PRAZO', 'Compreende as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9184, 1, '2.2.8.1.1.00.00.00.00.00', 'ADIANTAMENTOS DE CLIENTES A LONGO PRAZO- CONSOLIDAÇÃO', 'Compreende as antecipações recebidas por operações de fornecimento de bens ou prestação de serviços e que ensejem a devolução da quantia recebida, caso a operação não ocorra. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9185, 1, '2.2.8.2.0.00.00.00.00.00', 'OBRIGAÇÕES POR DANOS A TERCEIROS A LONGO PRAZO', 'Compreende os valores exigíveis no longo prazo, provenientes de danos a terceiros.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9186, 1, '2.2.8.2.1.00.00.00.00.00', 'OBRIGAÇÕES POR DANOS A TERCEIROS A LONGO PRAZO - CONSOLIDAÇÃO', 'Compreende os valores exigíveis no longo prazo, provenientes de danos a terceiros. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9187, 1, '2.2.8.3.0.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A LONGO PRAZO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9188, 1, '2.2.8.3.1.00.00.00.00.00', 'DEBÊNTURES E OUTROS TÍTULOS DE DÍVIDA A LONGO PRAZO- CONSOLIDAÇÃO', 'Compreende os títulos emitidos pela entidade que conferirão aos seus titulares direito de credito contra ela, nas condições constantes da escritura de emissão do titulo, com vencimento no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'X');
insert into contabilidade.planocontadetalhe values (9189, 1, '2.2.8.4.0.00.00.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL', 'Compreende os recursos recebidos pela entidade de seus acionistas ou quotistas destinados a serem utilizados para aumento de capital, quando haja a possibilidade de devolução destes recursos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9252, 1, '2.3.3.9.1.00.00.00.00.00', 'OUTRAS RESERVAS DE CAPITAL - CONSOLIDAÇÃO', 'Compreende os valores das demais reservas de capital, inclusive aquelas que terão seus saldos realizados por haverem sido extintas pela legislação. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9190, 1, '2.2.8.4.1.00.00.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL - CONSOLIDAÇÃO', 'Compreende os recursos recebidos pela entidade de seus acionistas ou quotistas destinados a serem utilizados para aumento de capital, quando haja a possibilidade de devolução destes recursos. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'P');
insert into contabilidade.planocontadetalhe values (9191, 1, '2.2.8.6.0.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP- LONGO PRAZO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto à referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9192, 1, '2.3.2.0.2.00.00.00.00.00', 'ADIANTAMENTO PARA FUTURO AUMENTO DE CAPITAL - INTRA OFSS', 'Compreende os recursos recebidos pela entidade de seus acionistas ou quotistas destinados a serem utilizados para aumento de capital, quando não haja a possibilidade de devolução destes recursos. Compreende os saldos que serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) do mesmo ente.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9193, 1, '2.2.8.6.1.00.00.00.00.00', 'OBRIGAÇÕES DECORRENTES DE CONTRATOS DE PPP- CONSOLIDAÇÃO', 'Compreende os passivos decorrentes dos contratos de Parceria Público Privada - PPP. Deve abranger tanto a contraprestação referente à incorporação dos ativos quanto à referente à prestação dos serviços objeto do contrato, incluindo a parcela variável condicionada à qualidade do serviço. Compreende os valores de operações efetuadas entre uma unidade pertencente ao orçamento fiscal e da seguridade social (OFSS) com pessoa ou unidade que não pertença ao OFSS de ente público, alem dos demais fatos que não compreendem transações entre partes.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9194, 1, '2.2.8.8.0.00.00.00.00.00', 'VALORES RESTITUÍVEIS', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no longo prazo.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9195, 1, '2.2.8.8.1.00.00.00.00.00', 'VALORES RESTITUÍVEIS - CONSOLIDAÇÃO', 'Compreende os valores de terceiros ou retenções em nome deles, quando a entidade do setor público for fiel depositaria, exigíveis no longo prazo. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
