insert into contabilidade.planocontadetalhe values (9325, 1, '4.2.1.1.1.01.00.00.00.00', 'CONTRIBUIÇÕES PATRONAIS AO RPPS', 'Compreende a contribuição patronal destinada ao custeio do Regime Próprio de Previdência Social.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9331, 1, '4.1.0.0.0.00.00.00.00.00', 'IMPOSTOS, TAXAS E CONTRIBUIÇÕES DE MELHORIA', 'Compreende toda prestação pecuniária compulsória, em moeda ou cujo valor nela se possa exprimir, que não constitua sanção de ato ilícito, instituída em lei e cobrada mediante atividade administrativa plenamente vinculada. Como: impostos, taxas e contribuições de melhoria.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9332, 1, '4.1.1.0.0.00.00.00.00.00', 'IMPOSTOS', 'Compreende como imposto o tributo cuja obrigação tem por fato gerador uma situação independente de qualquer atividade estatal especifica, relativa ao contribuinte.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9333, 1, '4.1.1.1.0.00.00.00.00.00', 'IMPOSTOS SOBRE COMERCIO EXTERIOR', 'Compreende os impostos sobre operações de comercio exterior, como por exemplo: impostos sobre a importação e imposto sobre a exportação.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9334, 1, '4.1.1.1.1.00.00.00.00.00', 'IMPOSTOS SOBRE COMERCIO EXTERIOR - CONSOLIDAÇÃO', 'Compreende os impostos sobre operações de comercio exterior, como exemplo: impostos sobre a importação e imposto sobre a exportação. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9335, 1, '4.1.1.2.0.00.00.00.00.00', 'IMPOSTOS SOBRE PATRIMÔNIO E A RENDA', 'Compreende os impostos sobre patrimônio e a renda, como por exemplo: imposto sobre a propriedade territorial rural, imposto sobre a propriedade predial e territorial urbana, imposto sobre a transmissão de bens imóveis e de direitos a eles relativos e impostos sobre a renda e proventos de qualquer natureza.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9336, 1, '4.1.1.2.1.00.00.00.00.00', 'IMPOSTOS SOBRE PATRIMÔNIO E A RENDA - CONSOLIDAÇÃO', 'Compreende os impostos sobre patrimônio e a renda, como exemplo: imposto sobre a propriedade territorial rural,imposto sobre a propriedade predial e territorial urbana,imposto sobre a transmissão de bens imóveis e de direitos a eles relativos e imposto sobre a renda e proventos de qualquer natureza. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9337, 1, '4.1.1.2.1.03.00.00.00.00', 'IMPOSTO SOBRE A RENDA E PROVENTOS DE QUALQUER NATUREZA', 'Registra a variação patrimonial aumentativa decorrente de imposto sobre a renda e proventos de qualquer natureza, retidos na fonte.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9338, 1, '4.1.1.3.0.00.00.00.00.00', 'IMPOSTOS SOBRE A PRODUÇÃO E A CIRCULAÇÃO', 'Compreende os impostos sobre a produção e a circulação, como por exemplo: imposto sobre produtos industrializados; imposto sobre operações de credito, câmbio e seguro, e sobre operações relativas a títulos e valores mobiliários; imposto sobre serviços de qualquer natureza.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9339, 1, '4.1.1.3.1.00.00.00.00.00', 'IMPOSTOS SOBRE A PRODUÇÃO E A CIRCULAÇÃO - CONSOLIDAÇÃO', 'Compreende os impostos sobre a produção e a circulação como, por exemplo, imposto sobre produtos industrializados;imposto sobre operações de crédito, cambio e seguro, e sobre operações relativas a títulos e valores mobiliários, imposto sobre serviços de qualquer natureza.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9340, 1, '4.1.1.4.0.00.00.00.00.00', 'IMPOSTOS EXTRAORDINÁRIOS', 'Compreende os impostos instituídos pela União, temporariamente, na iminência ou no caso de guerra externa, suprimidos, gradativamente, no prazo Maximo de cinco anos, contados da celebração da paz.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9341, 1, '4.1.1.4.1.00.00.00.00.00', 'IMPOSTOS EXTRAORDINÁRIOS - CONSOLIDAÇÃO', 'Compreende os impostos instituídos pela União, temporariamente, na iminência ou no caso de guerra externa, suprimidos,gradativamente, no prazo Maximo de cinco anos, contados da celebração da paz.Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9342, 1, '4.1.1.9.0.00.00.00.00.00', 'OUTROS IMPOSTOS', 'Compreende os demais impostos não classificados nos grupos anteriores.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9343, 1, '4.1.1.9.1.00.00.00.00.00', 'OUTROS IMPOSTOS - CONSOLIDAÇÃO', 'Compreende os demais impostos não classificados nos grupos anteriores. Compreende os valores de operações efetuadas entre uma unidade pertinente ao orçamento fiscal e da seguridade social (OFSS) com pessoa ou unidade que não pertença ao OFSS de ente público, alem dos demais fatos que não compreendem transações entre partes.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9344, 1, '4.1.2.0.0.00.00.00.00.00', 'TAXAS', 'Compreende as taxas cobradas pela União, pelos estados, pelo distrito federal ou pelos municípios, no âmbito de suas respectivas atribuições, tem como fato gerador o exercício regular do poder de policia, ou a utilização, efetiva ou potencial, de serviço público específico e divisível, prestado ao contribuinte ou posto a sua disposição.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9345, 1, '4.1.2.1.0.00.00.00.00.00', 'TAXAS PELO EXERCÍCIO DO PODER DE POLICIA', 'Compreende as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a prática de ato ou abstenção de fato, em razão de interesse público.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9346, 1, '4.1.2.1.1.00.00.00.00.00', 'TAXAS PELO EXERCÍCIO DO PODER DE POLICIA - CONSOLIDAÇÃO', 'Compreende as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9347, 1, '4.1.2.1.1.11.00.00.00.00', 'TAXA DE FISCALIZAÇÃO DE VIGILÂNCIA SANITÁRIA', 'Compreende as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxa de Fiscalização de Vigilância Sanitária.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9348, 1, '4.1.2.1.1.15.00.00.00.00', 'TAXA DE SERVIÇOS ADMINISTRATIVO', 'Compreende as taxas pelo poder de policia aquelas decorrentes das atividades da administração pública que, limitando ou disciplinando direito, interesse ou liberdade, regula a pratica de ato ou abstenção de fato em razão de interesse público. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS) - Taxas de Serviços Administrativos.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9349, 1, '4.1.2.2.0.00.00.00.00.00', 'TAXAS PELA PRESTAÇÃO DE SERVIÇOS', 'Compreende as taxas cobradas pela União, pelos estados, pelo distrito federal ou pelos municípios, no âmbito de suas respectivas atribuições, tem como fato gerador a utilização, efetiva ou potencial, de serviço público específico e divisível, prestado ao contribuinte ou posto a sua disposição.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9350, 1, '4.2.1.1.4.00.00.00.00.00', 'CONTRIBUIÇÕES SOCIAIS - RPPS - INTER OFSS - ESTADO', 'Compreende a contribuição patronal e a contribuição do segurado destinada ao custeio do regime próprio de previdência social. Compreende os saldos que serão excluídos nos demonstrativos consolidados do Orçamento Fiscal e da Seguridade Social (OFSS) de entes públicos distintos, resultantes das transações entre o ente e um estado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9351, 1, '4.1.2.2.1.00.00.00.00.00', 'TAXAS PELA PRESTAÇÃO DE SERVIÇOS - CONSOLIDAÇÃO', 'Compreende as taxas cobradas pela União, pelos estados, pelo distrito federal ou pelos municípios, no âmbito de suas respectivas atribuições tem como fato gerador a utilização, efetiva ou potencial, de serviço público específico e divisível prestado ao contribuinte ou posto a sua disposição. Compreende os saldos que não serão excluídos nos demonstrativos consolidados do orçamento fiscal e da seguridade social (OFSS).', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9352, 1, '4.1.3.0.0.00.00.00.00.00', 'CONTRIBUIÇÕES DE MELHORIA', 'Compreende como contribuição de melhoria o tributo cobrado pela União, pelos estados, pelo distrito federal ou pelos municípios, no âmbito de suas respectivas atribuições, sendo instituída para fazer face ao custo de obras públicas de que decorra valorização imobiliária, tendo como limite total a despesa realizada e como limite individual o acréscimo de valor que da obra resultar para cada imóvel beneficiado.', 2, false, 0, 'N');
insert into contabilidade.planocontadetalhe values (9353, 1, '4.1.3.1.0.00.00.00.00.00', 'CONTRIBUIÇÃO DE MELHORIA PELA EXPANSÃO DA REDE DE ÁGUA POTÁVEL E ESGOTO SANITÁRIO', 'Compreende o valor da arrecadação de receita de contribuição de melhoria decorrente de valorização de propriedades em função da expansão da rede de água potável e esgoto sanitário.', 2, false, 0, 'N');
